import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState({
    Nombre: '',
    Precio: '',
    Disponibilidad: '1',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Cantidad: '1'
  });
  const [editando, setEditando] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error('Error al cargar productos');
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Error al cargar productos', err);
      setError('No se pudieron cargar los productos');
    } finally {
      setCargando(false);
    }
  };

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNuevo(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const validarFormulario = () => {
    const errores = [];
    
    if (!nuevo.Nombre.trim()) {
      errores.push('El nombre del producto es requerido');
    }
    
    if (!nuevo.Precio || isNaN(nuevo.Precio) || parseFloat(nuevo.Precio) <= 0) {
      errores.push('El precio debe ser un número mayor a 0');
    }
    
    if (!nuevo.Cantidad || isNaN(nuevo.Cantidad) || parseInt(nuevo.Cantidad) <= 0) {
      errores.push('La cantidad debe ser un número mayor a 0');
    }
    
    if (!nuevo.Tamaño) {
      errores.push('Seleccione un tamaño');
    }
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    
    return true;
  };

  const prepararDatosParaEnviar = () => {
    return {
      ...nuevo,
      Precio: parseFloat(nuevo.Precio),
      Cantidad: parseInt(nuevo.Cantidad),
      Disponibilidad: parseInt(nuevo.Disponibilidad),
      Calorias: nuevo.Calorias ? parseInt(nuevo.Calorias) : null
    };
  };

  const agregarProducto = async () => {
    if (!validarFormulario()) return;

    try {
      const productoExistente = items.find(item => 
        item.Nombre.toLowerCase() === nuevo.Nombre.toLowerCase() && 
        item.Tamaño === nuevo.Tamaño
      );

      if (productoExistente) {
        // Actualizar producto existente
        const nuevaCantidad = parseInt(productoExistente.Cantidad) + parseInt(nuevo.Cantidad);
        
        const response = await fetch(`http://localhost:8000/producto/${productoExistente.id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ 
            Cantidad: nuevaCantidad,
            Disponibilidad: parseInt(nuevo.Disponibilidad)
          }),
        });

        if (!response.ok) throw new Error('Error al actualizar producto');

        const data = await response.json();
        setItems(items.map(item => 
          item.id === productoExistente.id ? data : item
        ));
        alert(`Se actualizó el producto ${nuevo.Nombre}`);
      } else {
        // Crear nuevo producto
        const response = await fetch('http://localhost:8000/producto', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(prepararDatosParaEnviar()),
        });

        if (!response.ok) throw new Error('Error al crear producto');

        const data = await response.json();
        setItems([...items, data]);
        alert('Producto creado exitosamente');
      }

      // Resetear formulario
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: '1',
        Descripcion: '',
        Tamaño: '',
        Calorias: '',
        Cantidad: '1'
      });
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al procesar la solicitud');
    }
  };

  const iniciarEdicion = (producto) => {
    setEditando(producto);
    setNuevo({
      Nombre: producto.Nombre,
      Precio: producto.Precio.toString(),
      Disponibilidad: producto.Disponibilidad.toString(),
      Descripcion: producto.Descripcion,
      Tamaño: producto.Tamaño,
      Calorias: producto.Calorias?.toString() || '',
      Cantidad: producto.Cantidad.toString()
    });
  };

  const cancelarEdicion = () => {
    setEditando(null);
    setNuevo({
      Nombre: '',
      Precio: '',
      Disponibilidad: '1',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Cantidad: '1'
    });
  };

  const actualizarProducto = async () => {
    if (!validarFormulario()) return;

    try {
      const response = await fetch(`http://localhost:8000/producto/${editando.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(prepararDatosParaEnviar()),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Error al actualizar producto');
      }

      const data = await response.json();
      setItems(items.map(item => item.id === editando.id ? data : item));
      
      setEditando(null);
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: '1',
        Descripcion: '',
        Tamaño: '',
        Calorias: '',
        Cantidad: '1'
      });
      
      alert('Producto actualizado exitosamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al actualizar el producto');
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este producto?')) return;

    try {
      const response = await fetch(`http://localhost:8000/producto/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Error al eliminar producto');

      setItems(items.filter(item => item.id !== id));
      
      if (editando && editando.id === id) {
        cancelarEdicion();
      }
      
      alert('Producto eliminado exitosamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al eliminar el producto');
    }
  };

  if (cargando) {
    return <div className="cargando">Cargando productos...</div>;
  }

  return (
    <div className="inventario-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S´ FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin🥤</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        {error && <div className="error-message">{error}</div>}

        <div className="formulario-producto">
          <div className="form-group">
            <label>Nombre del producto</label>
            <input
              name="Nombre"
              type="text"
              value={nuevo.Nombre}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Precio</label>
            <input
              name="Precio"
              type="number"
              min="0.01"
              step="0.01"
              value={nuevo.Precio}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Cantidad</label>
            <input
              name="Cantidad"
              type="number"
              min="1"
              value={nuevo.Cantidad}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Descripción</label>
            <input
              name="Descripcion"
              value={nuevo.Descripcion}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Tamaño</label>
            <select
              name="Tamaño"
              value={nuevo.Tamaño}
              onChange={manejarCambio}
            >
              <option value="" disabled>Seleccione un tamaño</option>
              <option value="100">100 ml</option>
              <option value="150">150 ml</option>
              <option value="250">250 ml</option>
            </select>
          </div>

          <div className="form-group">
            <label>Calorías</label>
            <input
              name="Calorias"
              type="number"
              min="0"
              value={nuevo.Calorias}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Disponibilidad</label>
            <select
              name="Disponibilidad"
              value={nuevo.Disponibilidad}
              onChange={manejarCambio}
            >
              <option value="1">Disponible</option>
              <option value="0">No disponible</option>
            </select>
          </div>

          <div className="acciones-formulario">
            {editando ? (
              <>
                <button 
                  onClick={actualizarProducto}
                  className="btn-guardar"
                >
                  Guardar Cambios
                </button>
                <button 
                  onClick={cancelarEdicion}
                  className="btn-cancelar"
                >
                  Cancelar
                </button>
              </>
            ) : (
              <button 
                onClick={agregarProducto}
                className="btn-agregar"
              >
                {editando ? 'Actualizar Producto' : 'Agregar Producto'}
              </button>
            )}
          </div>
        </div>

        <div className="tabla-productos">
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Precio</th>
                <th>Descripción</th>
                <th>Tamaño</th>
                <th>Calorías</th>
                <th>Cantidad</th>
                <th>Disponible</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {items.length === 0 ? (
                <tr>
                  <td colSpan="8" className="no-items">
                    No hay productos registrados
                  </td>
                </tr>
              ) : (
                items.map((item) => (
                  <tr key={item.id}>
                    <td>{item.Nombre}</td>
                    <td>${parseFloat(item.Precio).toFixed(2)}</td>
                    <td>{item.Descripcion}</td>
                    <td>{item.Tamaño} ml</td>
                    <td>{item.Calorias || '-'}</td>
                    <td>{item.Cantidad}</td>
                    <td>
                      <span className={`disponibilidad ${item.Disponibilidad ? 'disponible' : 'no-disponible'}`}>
                        {item.Disponibilidad ? 'Sí' : 'No'}
                      </span>
                    </td>
                    <td className="acciones">
                      <button 
                        onClick={() => iniciarEdicion(item)}
                        className="btn-editar"
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => eliminarProducto(item.id)}
                        className="btn-eliminar"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Inventario;
