import React, { useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function PaginaBatidos() {
  const navigate = useNavigate();
  const [batidos, setBatidos] = useState([
    { id: 1, nombre: 'Batido de Fresa', precio: 5.99, descripcion: 'Delicioso batido de fresa natural' },
    { id: 2, nombre: 'Batido de Chocolate', precio: 6.49, descripcion: 'Batido cremoso de chocolate' },
    { id: 3, nombre: 'Batido de Vainilla', precio: 5.99, descripcion: 'Clásico batido de vainilla' },
    { id: 4, nombre: 'Batido de Mango', precio: 6.99, descripcion: 'Batido tropical de mango' },
    { id: 5, nombre: 'Batido de Cookies', precio: 7.49, descripcion: 'Batido con trozos de galleta' },
  ]);

  // Verificar autenticación al cargar el componente
  useEffect(() => {
    const isAuthenticated = localStorage.getItem('batidosAuth') === 'true';
    if (!isAuthenticated) {
      navigate('/loginbatidos');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('batidosAuth');
    localStorage.removeItem('userRole');
    navigate('/loginbatidos');
  };

  return (
    <div className="comprar-container">
      <nav className="menu-cliente">
        <div className="logo">
          <h2>S´ FOR YOUR NUTRITION - BATIDOS</h2>
        </div>
        <ul className="menu-links">
          <li><Link to="/pedidos">Pedidos</Link></li>
          <li><button onClick={handleLogout} className="logout-btn">Cerrar Sesión</button></li>
        </ul>
      </nav>

      <div className="contenido-principal">
        <h1>Lista de Batidos</h1>
        
        <div className="lista-batidos">
          {batidos.map(batido => (
            <div key={batido.id} className="item-batido">
              <h3>{batido.nombre}</h3>
              <p>{batido.descripcion}</p>
              <p className="precio">${batido.precio.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PaginaBatidos;
