import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState({
    Nombre: '',
    Precio: '',
    Disponibilidad: 1,
    Id_ingrediente: '',
    Id_cantidad_ingrediente: '',
    Descripcion: '',
    Tamaño: '',
    Calorias: ''
  });

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error('Error al cargar productos');
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Error al cargar productos', err);
    }
  };

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNuevo((prev) => ({ ...prev, [name]: value }));
  };

  const agregar = async () => {
    // Validación rápida
    if (
      !nuevo.Nombre || !nuevo.Precio || !nuevo.Id_ingrediente ||
      !nuevo.Id_cantidad_ingrediente || !nuevo.Descripcion ||
      !nuevo.Tamaño || !nuevo.Calorias
    ) {
      alert("Por favor completa todos los campos.");
      return;
    }

    try {
      const res = await fetch('http://localhost:8000/producto', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(nuevo)
      });

      if (!res.ok) throw new Error('Error al agregar producto');

      const nuevoProducto = await res.json();
      setItems([...items, nuevoProducto]);

      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: 1,
        Id_ingrediente: '',
        Id_cantidad_ingrediente: '',
        Descripcion: '',
        Tamaño: '',
        Calorias: ''
      });
    } catch (error) {
      console.error('Error al agregar producto', error);
    }
  };

  return (
    <div>
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S´ FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin🥤</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        <div className="formulario">
          <input name="Nombre" placeholder="Nombre" value={nuevo.Nombre} onChange={manejarCambio} />
          <input name="Precio" type="number" placeholder="Precio" value={nuevo.Precio} onChange={manejarCambio} />
          <input name="Id_ingrediente" placeholder="ID Ingrediente" value={nuevo.Id_ingrediente} onChange={manejarCambio} />
          <input name="Id_cantidad_ingrediente" placeholder="ID Cantidad" value={nuevo.Id_cantidad_ingrediente} onChange={manejarCambio} />
          <input name="Descripcion" placeholder="Descripción" value={nuevo.Descripcion} onChange={manejarCambio} />
          <input name="Tamaño" placeholder="Tamaño" value={nuevo.Tamaño} onChange={manejarCambio} />
          <input name="Calorias" placeholder="Calorías" value={nuevo.Calorias} onChange={manejarCambio} />
          <button onClick={agregar}>Agregar</button>
        </div>

        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Descripción</th>
              <th>Ingrediente</th>
              <th>Cantidad Ing</th>
              <th>Tamaño</th>
              <th>Calorías</th>
            </tr>
          </thead>
          <tbody>
            {items.length === 0 ? (
              <tr><td colSpan="7">No hay productos registrados</td></tr>
            ) : (
              items.map((item, index) => (
                <tr key={item.id || index}>
                  <td>{item.Nombre}</td>
                  <td>{item.Precio}</td>
                  <td>{item.Descripcion}</td>
                  <td>{item.Id_ingrediente}</td>
                  <td>{item.Id_cantidad_ingrediente}</td>
                  <td>{item.Tamaño}</td>
                  <td>{item.Calorias}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventario;
