import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/notificaciones.css';

function Notificaciones() {
  const location = useLocation();
  const isAdmin = location.pathname.includes('admin');
  const [nueva, setNueva] = useState({
    rol: 'Preparador de batidos',
    mensaje: '',
    tipo: 'info', 
    estado: 'preparando',
  });

  const [notificaciones, setNotificaciones] = useState([
    {
      rol: 'Preparador de batidos',
      mensaje: 'Tu pedido #123 est√° en preparaci√≥n...',
      tipo: 'info',
      estado: 'preparando',
    },
    {
      rol: 'Sistema',
      mensaje: '¬°Tenemos nuevos productos disponibles!',
      tipo: 'promocion',
    },
  ]);

  useEffect(() => {
    // Mostrar notificaci√≥n de bienvenida solo para clientes
    if (!isAdmin) {
      setNotificaciones(prev => [
        {
          rol: 'Sistema',
          mensaje: '¬°Bienvenido a S¬¥ FOR YOUR NUTRITION! ¬øListo para ordenar?',
          tipo: 'bienvenida',
          accion: {
            texto: 'Ver Productos',
            ruta: '/comprar'
          }
        },
        ...prev
      ]);
    }
  }, [isAdmin]);

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNueva((prev) => ({ ...prev, [name]: value }));
  };

  const registrarNotificacion = () => {
    if (nueva.mensaje && nueva.rol) {
      setNotificaciones([...notificaciones, nueva]);
      setNueva({
        rol: 'Preparador de batidos',
        mensaje: '',
        tipo: 'info',
        estado: 'preparando',
      });
    }
  };

  return (
    <div className="notificaciones-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S¬¥ FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            {isAdmin && <li><Link to="/inventario">Inventario</Link></li>}
            <li><Link to={isAdmin ? "/micuentaadmin" : "/mi-cuenta"}>Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-notificaciones">
        <h1 className="titulo-bienvenida">
          {isAdmin ? 'Bienvenido nuevamente Admin ü•§' : 'Tus Notificaciones üéâ'}
        </h1>

        {isAdmin && (
          <div className="panel-creacion">
            <h2>Centro de Notificaciones</h2>
            <div className="formulario-notificacion">
              <select name="rol" value={nueva.rol} onChange={manejarCambio}>
                <option value="Preparador de batidos">Preparador de batidos</option>
                <option value="Empleado">Empleado</option>
                <option value="Sistema">Sistema</option>
              </select>
              <select name="tipo" value={nueva.tipo} onChange={manejarCambio}>
                <option value="info">Informaci√≥n</option>
                <option value="alerta">Alerta</option>
                <option value="promocion">Promoci√≥n</option>
              </select>
              {nueva.rol === 'Preparador de batidos' && (
                <select name="estado" value={nueva.estado} onChange={manejarCambio}>
                  <option value="preparando">Preparando</option>
                  <option value="listo">Listo</option>
                </select>
              )}
              <input
                type="text"
                name="mensaje"
                placeholder="Mensaje de notificaci√≥n"
                value={nueva.mensaje}
                onChange={manejarCambio}
              />
              <button onClick={registrarNotificacion}>Registrar</button>
            </div>
          </div>
        )}

        <div className="lista-notificaciones">
          <h2>{isAdmin ? 'Notificaciones Actuales' : 'Tus Alertas'}</h2>
          <ul>
            {notificaciones.map((n, idx) => (
              <li key={idx} className={`notificacion ${n.tipo}`}>
                <div className="notificacion-contenido">
                  <span className="notificacion-rol">{n.rol}</span>
                  <span className="notificacion-icono">
                    {n.tipo === 'alerta' ? 'üö®' : 
                     n.tipo === 'promocion' ? 'üéÅ' : 
                     n.tipo === 'bienvenida' ? 'üëã' : '‚ÑπÔ∏è'}
                  </span>
                  <span className="notificacion-mensaje">{n.mensaje}</span>
                  {n.estado && <span className="notificacion-estado">({n.estado})</span>}
                </div>
                {n.accion && (
                  <Link to={n.accion.ruta} className="notificacion-accion">
                    {n.accion.texto} ‚Üí
                  </Link>
                )}
              </li>
            ))}
          </ul>

          {!isAdmin && (
            <div className="call-to-action">
              <h3>¬øListo para disfrutar de nuestros productos?</h3>
              <Link to="/comprar" className="btn-ordenar">
                Ver Productos y Ordenar Ahora
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Notificaciones;
