import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/register.css';

function RegistroCliente() {
  const [formData, setFormData] = useState({
    Documento: '',
    Nombres: '',
    Apellidos: '',
    Telefono: '',
    Email: '',
    Direccion: '',
    Id_ciudad: '',
    Contraseña: '',
    Id_tipo_compra: 1 // Por defecto, por ejemplo: 1 = compra normal
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // 1. Registrar la persona
      const personaResponse = await fetch('http://localhost:8000/personas', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const personaResult = await personaResponse.json();

      if (!personaResponse.ok) {
        alert('Error al registrar persona: ' + (personaResult.error || ''));
        return;
      }

      const idPersona = personaResult.Id || personaResult.id || personaResult.Id_persona;

      // 2. Registrar al cliente con el Id_persona
      const clienteData = {
        Id_persona: idPersona,
        Id_tipo_compra: formData.Id_tipo_compra,
        Puntos_fidelizacion: 0,
        Descuento_inicial: 0
      };

      const clienteResponse = await fetch('http://localhost:8000/clientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(clienteData)
      });

      const clienteResult = await clienteResponse.json();

      if (clienteResponse.ok) {
        alert('Cliente registrado correctamente');
        navigate('/ingresar');
      } else {
        alert('Error al registrar cliente: ' + (clienteResult.error || ''));
      }
    } catch (err) {
      alert('Error al conectar con el servidor');
    }
  };

  return (
    <div className="auth-container">
      <h2>Registro de Cliente</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          name="Documento"
          placeholder="Documento"
          value={formData.Documento}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="Nombres"
          placeholder="Nombres"
          value={formData.Nombres}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="Apellidos"
          placeholder="Apellidos"
          value={formData.Apellidos}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="Telefono"
          placeholder="Teléfono"
          value={formData.Telefono}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="Email"
          placeholder="Email"
          value={formData.Email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="Direccion"
          placeholder="Dirección"
          value={formData.Direccion}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="Id_ciudad"
          placeholder="Ciudad"
          value={formData.Id_ciudad}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="Contraseña"
          placeholder="Contraseña"
          value={formData.Contraseña}
          onChange={handleChange}
          required
        />
        <button type="submit">Registrar Cliente</button>
      </form>
    </div>
  );
}

export default RegistroCliente;
