import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  return (
    <div className="menu-container">
      {/* Menú principal desplegable a la izquierda */}
      <div className="dropdown-menu">
        <button 
          className="dropdown-toggle" 
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          Menú
        </button>
        
        {isMenuOpen && (
          <div className="dropdown-content">
            <Link to="/" onClick={() => setIsMenuOpen(false)}>Inicio</Link>
            <Link to="/acerca" onClick={() => setIsMenuOpen(false)}>Acerca de nosotros</Link>
            <Link to="/contacto" onClick={() => setIsMenuOpen(false)}>Contactanos</Link>
            <Link to="/login" onClick={() => setIsMenuOpen(false)}>Iniciar sesión</Link>
          </div>
        )}
      </div>

      {/* Resto del menú (Trabajos, Proyectos, etc.) */}
      <nav className="navbar">
        <div className="menu-item">
          <span>Trabajos</span>
          <div className="submenu">
            <Link to="/sub1">Sub Elemento #1</Link>
            <Link to="/sub2">Sub Elemento #2</Link>
          </div>
        </div>
        
        <div className="menu-item">
          <span>Proyectos</span>
          <div className="submenu">
            <Link to="/sub3">Sub Elemento #3</Link>
          </div>
        </div>
        
        <div className="menu-item">
          <span>Responsive</span>
        </div>
      </nav>
    </div>
  );
}

export default Menu;
