import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/register.css';

function LoginBatidos() {
  const [formData, setFormData] = useState({
    usuario: '',
    contraseña: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Verificación simple (en producción usaría tu backend)
      if (formData.usuario === 'batidos' && formData.contraseña === 'batidos123') {
        localStorage.setItem('userRole', 'batidos');
        localStorage.setItem('batidosAuth', 'true');
        navigate('/paginabatidos');
      } else {
        alert('Credenciales incorrectas');
      }
    } catch (error) {
      alert('Error al conectar con el servidor');
      console.error(error);
    }
  };

  return (
    <div className="login-container">
      <h2>Acceso Preparador de Batidos</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Usuario:
          <input
            type="text"
            name="usuario"
            value={formData.usuario}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Contraseña:
          <input
            type="password"
            name="contraseña"
            value={formData.contraseña}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
}

export default LoginBatidos;
