import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const [isOpen, setIsOpen] = useState(false);
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <div className="original-menu-container">
      {/* Botón de menú hamburguesa */}
      <div className="original-menu-toggle" onClick={toggleMenu}>
        <div className={`original-menu-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`original-menu-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`original-menu-line ${isOpen ? 'open' : ''}`}></div>
      </div>

      {/* Menú lateral */}
      <div className={`original-sidebar ${isOpen ? 'open' : ''}`}>
        <div className="original-brand-header">
          <h1>S' FOR YOUR NUTRITION</h1>
          <h2>Bienvenido nuevamente</h2>
        </div>

        <div className="original-menu-content">
          <div className="original-menu-links">
            <Link to="/productos" onClick={toggleMenu}>Productos</Link>
            <Link to="/ordenar" onClick={toggleMenu}>Ordenar</Link>
            <Link to="/carrito" onClick={toggleMenu}>Carrito</Link>
            <Link to="/mi-cuenta" onClick={toggleMenu}>Mi Cuenta</Link>
          </div>

          <div className="original-products-section">
            <h3>Nuestros Productos</h3>
            <div className="original-product-item">
              <span>Cupcake de vainilla</span>
              <span>$8500</span>
            </div>
            <div className="original-product-item">
              <span>Cupcake de Avena</span>
              <span>$8000</span>
            </div>
            <div className="original-product-item">
              <span>Malteada</span>
              <span>$12000</span>
            </div>
          </div>
        </div>
      </div>

      {/* Overlay */}
      {isOpen && <div className="original-menu-overlay" onClick={toggleMenu} />}
    </div>
  );
}

export default Menu;
