import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/register.css';

function Pedidos() {
  const [pedidos, setPedidos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);
  const [filtro, setFiltro] = useState('todos');

  const cargarPedidos = async (estado = null) => {
    try {
      setCargando(true);
      setError(null);
      
      const url = estado 
        ? `http://localhost/back_your_nutrition/public/api/pedidos/estado/${estado}`
        : 'http://localhost/back_your_nutrition/public/api/pedidos';
      
      const response = await fetch(url);
      
      if (!response.ok) throw new Error(`Error HTTP: ${response.status}`);
      
      const data = await response.json();
      setPedidos(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => { cargarPedidos(); }, []);

  const actualizarEstado = async (pedidoId, nuevoEstado) => {
    try {
      const response = await fetch(
        `http://localhost/back_your_nutrition/public/api/pedidos/${pedidoId}`, 
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ estado: nuevoEstado })
        }
      );

      if (!response.ok) throw new Error('Error al actualizar estado');

      setPedidos(pedidos.map(p => 
        p.id === pedidoId ? { ...p, estado: nuevoEstado } : p
      ));
    } catch (err) {
      setError(err.message);
    }
  };

  const getEstadoStyle = (estado) => {
    const estilos = {
      pendiente: 'bg-yellow-100 text-yellow-800 border-l-yellow-500',
      preparacion: 'bg-blue-100 text-blue-800 border-l-blue-500',
      completado: 'bg-green-100 text-green-800 border-l-green-500',
      cancelado: 'bg-red-100 text-red-800 border-l-red-500'
    };
    return estilos[estado] || 'bg-gray-100 text-gray-800 border-l-gray-500';
  };

  if (cargando) return (
    <div className="p-4 text-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
      <p>Cargando pedidos...</p>
    </div>
  );

  if (error) return (
    <div className="p-4 bg-red-100 text-red-700 rounded-lg mx-4">
      <h3 className="font-bold">Error</h3>
      <p>{error}</p>
      <button onClick={() => cargarPedidos()} className="mt-2 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600">
        Reintentar
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h2 className="text-xl font-bold">S´ FOR YOUR NUTRITION</h2>
            </div>
            <div className="flex space-x-4">
              <Link to="/admin/pedidos" className="px-3 py-2 rounded-md text-sm font-medium bg-gray-200">Pedidos</Link>
              <Link to="/admin/notificaciones" className="px-3 py-2 rounded-md text-sm font-medium">Notificaciones</Link>
              <Link to="/admin/inventario" className="px-3 py-2 rounded-md text-sm font-medium">Inventario</Link>
              <Link to="/admin/micuenta" className="px-3 py-2 rounded-md text-sm font-medium">Mi Cuenta</Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold">Gestión de Pedidos</h2>
            <select
              value={filtro}
              onChange={(e) => {
                setFiltro(e.target.value);
                cargarPedidos(e.target.value === 'todos' ? null : e.target.value);
              }}
              className="p-2 border rounded"
            >
              <option value="todos">Todos</option>
              <option value="pendiente">Pendientes</option>
              <option value="preparacion">En preparación</option>
              <option value="completado">Completados</option>
              <option value="cancelado">Cancelados</option>
            </select>
          </div>

          {pedidos.length === 0 ? (
            <p>No hay pedidos {filtro !== 'todos' ? `con estado "${filtro}"` : 'registrados'}.</p>
          ) : (
            <div className="space-y-4">
              {pedidos.map(pedido => (
                <div key={pedido.id} className={`p-4 border rounded-lg ${getEstadoStyle(pedido.estado)} border-l-4`}>
                  {/* Contenido del pedido */}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Pedidos;
