import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/paginaCliente.css';

function PaginaCliente() {
  const [mostrarBienvenida, setMostrarBienvenida] = useState(false);

  // Verificar si es la primera vez que entra el cliente
  useEffect(() => {
    const primeraVez = localStorage.getItem('primeraVezCliente') === null;
    setMostrarBienvenida(primeraVez);
    
    if (primeraVez) {
      localStorage.setItem('primeraVezCliente', 'false');
    }
  }, []);

  return (
    <div className="pagina-cliente">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S´ FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/comprar">Ordenar</Link></li>
            <li><Link to="/carrito">Carrito</Link></li>
            <li><Link to="/mi-cuenta">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-principal">
        {mostrarBienvenida && (
          <div className="bienvenida-container">
            <div className="mensaje-bienvenida">
              <h1>¡Bienvenido a S´ FOR YOUR NUTRITION!</h1>
              <p>¿Listo para ordenar?</p>
              <Link to="/comprar" className="btn-ordenar">
                Ir a Ordenar
              </Link>
            </div>
          </div>
        )}
        
        {/* Resto del contenido de la página */}
        <div className="productos-destacados">
          <h2>Nuestros Productos Destacados</h2>
          {/* Aquí iría el listado de productos */}
        </div>
      </div>
    </div>
  );
}

export default PaginaCliente;
