import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState({
    Nombre: '',
    Precio: '',
    Disponibilidad: 1,
    Descripcion: '',
    Tama침o: '',
    Calorias: '',
    Cantidad: 1
  });
  const [editando, setEditando] = useState(null);

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error('Error al cargar productos');
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Error al cargar productos', err);
    }
  };

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNuevo((prev) => ({ ...prev, [name]: value }));
  };

  const agregar = async () => {
    if (
      !nuevo.Nombre || !nuevo.Precio || !nuevo.Descripcion ||
      !nuevo.Tama침o || !nuevo.Calorias || !nuevo.Cantidad
    ) {
      alert("Por favor completa todos los campos.");
      return;
    }

    // Verificar si el producto ya existe
    const productoExistente = items.find(item => 
      item.Nombre.toLowerCase() === nuevo.Nombre.toLowerCase() &&
      item.Tama침o === nuevo.Tama침o
    );

    try {
      if (productoExistente) {
        // Actualizar cantidad del producto existente
        const nuevaCantidad = parseInt(productoExistente.Cantidad) + parseInt(nuevo.Cantidad);
        
        const res = await fetch(`http://localhost:8000/productos/${productoExistente.id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ Cantidad: nuevaCantidad })
        });

        if (!res.ok) throw new Error('Error al actualizar cantidad del producto');

        // Actualizar lista local
        setItems(items.map(item => 
          item.id === productoExistente.id 
            ? { ...item, Cantidad: nuevaCantidad } 
            : item
        ));

        alert(`Se agregaron ${nuevo.Cantidad} unidades al producto existente`);
      } else {
        // Crear nuevo producto
        const res = await fetch('http://localhost:8000/productos', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(nuevo)
        });

        if (!res.ok) throw new Error('Error al agregar producto');

        const nuevoProducto = await res.json();
        setItems([...items, nuevoProducto]);
        alert('Producto agregado correctamente');
      }

      // Resetear formulario
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: 1,
        Descripcion: '',
        Tama침o: '',
        Calorias: '',
        Cantidad: 1
      });
    } catch (error) {
      console.error('Error al procesar producto', error);
      alert('Error al procesar el producto');
    }
  };

  const eliminarProducto = async (id) => {
    try {
      const res = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE'
      });

      if (!res.ok) throw new Error('Error al eliminar producto');

      setItems(items.filter(item => item.id !== id));
      
      if (editando && editando.id === id) {
        setEditando(null);
        setNuevo({
          Nombre: '',
          Precio: '',
          Disponibilidad: 1,
          Descripcion: '',
          Tama침o: '',
          Calorias: '',
          Cantidad: 1
        });
      }
    } catch (error) {
      console.error('Error al eliminar producto', error);
    }
  };

  const iniciarEdicion = (producto) => {
    setEditando(producto);
    setNuevo({
      Nombre: producto.Nombre,
      Precio: producto.Precio,
      Disponibilidad: producto.Disponibilidad,
      Descripcion: producto.Descripcion,
      Tama침o: producto.Tama침o,
      Calorias: producto.Calorias,
      Cantidad: producto.Cantidad
    });
  };

  const cancelarEdicion = () => {
    setEditando(null);
    setNuevo({
      Nombre: '',
      Precio: '',
      Disponibilidad: 1,
      Descripcion: '',
      Tama침o: '',
      Calorias: '',
      Cantidad: 1
    });
  };

  const actualizarProducto = async () => {
    if (
      !nuevo.Nombre || !nuevo.Precio || !nuevo.Descripcion ||
      !nuevo.Tama침o || !nuevo.Calorias || !nuevo.Cantidad
    ) {
      alert("Por favor completa todos los campos.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:8000/productos/${editando.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(nuevo)
      });

      if (!res.ok) throw new Error('Error al actualizar producto');

      const productoActualizado = await res.json();
      setItems(items.map(item => item.id === editando.id ? productoActualizado : item));
      
      setEditando(null);
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: 1,
        Descripcion: '',
        Tama침o: '',
        Calorias: '',
        Cantidad: 1
      });
    } catch (error) {
      console.error('Error al actualizar producto', error);
    }
  };

  return (
    <div>
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S췂 FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin游볷</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        <div className="formulario">
          <select 
            name="Nombre" 
            value={nuevo.Nombre} 
            onChange={manejarCambio}
            className={!nuevo.Nombre ? 'placeholder-selected' : ''}
          >
            <option value="" disabled hidden>Seleccione un producto</option>
            <option value="Cupcake de vainilla">Cupcake de vainilla</option>
            <option value="Cupcake de Avena">Cupcake de Avena</option>
            <option value="Malteada">Malteada</option>
            <option value="Batido de Fresa">Batido de Fresa</option>
            <option value="Batido de Chocolate">Batido de Chocolate</option>
          </select>

          <input name="Precio" type="number" placeholder="Precio" value={nuevo.Precio} onChange={manejarCambio} />
          <input name="Cantidad" type="number" placeholder="Cantidad" min="1" value={nuevo.Cantidad} onChange={manejarCambio} />
          
          <input name="Descripcion" placeholder="Descripci칩n" value={nuevo.Descripcion} onChange={manejarCambio} />
          
          <select 
            name="Tama침o" 
            value={nuevo.Tama침o} 
            onChange={manejarCambio}
            className={!nuevo.Tama침o ? 'placeholder-selected' : ''}
          >
            <option value="" disabled hidden>Seleccione un tama침o</option>
            <option value="100">100</option>
            <option value="150">150</option>
            <option value="250">250</option>
          </select>
          
          <input name="Calorias" placeholder="Calor칤as" value={nuevo.Calorias} onChange={manejarCambio} />
          
          <select name="Disponibilidad" value={nuevo.Disponibilidad} onChange={manejarCambio}>
            <option value="1">Disponible (1)</option>
            <option value="0">No disponible (0)</option>
          </select>
          
          {editando ? (
            <div className="acciones-edicion">
              <button onClick={actualizarProducto}>Guardar</button>
              <button onClick={cancelarEdicion} className="cancelar">Cancelar</button>
            </div>
          ) : (
            <button onClick={agregar}>Agregar</button>
          )}
        </div>

        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Descripci칩n</th>
              <th>Tama침o</th>
              <th>Calor칤as</th>
              <th>Cantidad</th>
              <th>Disponibilidad</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {items.length === 0 ? (
              <tr><td colSpan="8">No hay productos registrados</td></tr>
            ) : (
              items.map((item, index) => (
                <tr key={item.id || index}>
                  <td>{item.Nombre}</td>
                  <td>{item.Precio}</td>
                  <td>{item.Descripcion}</td>
                  <td>{item.Tama침o}</td>
                  <td>{item.Calorias}</td>
                  <td>{item.Cantidad}</td>
                  <td>{item.Disponibilidad === 1 ? 'Disponible' : 'No disponible'}</td>
                  <td className="acciones">
                    <button onClick={() => iniciarEdicion(item)} className="editar">Editar</button>
                    <button onClick={() => eliminarProducto(item.id)} className="eliminar">Eliminar</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventario;
