import React, { useState, useEffect } from 'react';
import '../style/register.css';
import '../style/micuenta.css';
import { Link, useNavigate } from 'react-router-dom';

function EditarPerfilCliente() {
  const [formData, setFormData] = useState({
    Documento: '',
    Nombres: '',
    Apellidos: '',
    Telefono: '',
    Email: '',
    Direccion: '',
    Id_ciudad: '',
    Contraseña: '',
    Foto: null
  });

  const [preview, setPreview] = useState(null);
  const navigate = useNavigate();
  // Asumimos que tienes un ID del cliente a editar, por ejemplo, desde la URL
  // const { id } = useParams(); 
  const clienteId = 1; // Reemplaza con la forma real de obtener el ID

  useEffect(() => {
    // Función para cargar los datos del cliente a editar
    const cargarDatosCliente = async () => {
      try {
        const response = await fetch(`http://localhost:8000/personas/${clienteId}`); // Ajusta la URL de tu API
        if (response.ok) {
          const data = await response.json();
          // Excluir la contraseña del formulario por seguridad (no la pre-llenamos para editar)
          const { Contraseña, ...datosParaFormulario } = data;
          setFormData({ ...datosParaFormulario, Foto: null }); // Inicializar Foto como null
          if (data.FotoUrl) {
            setPreview(`http://localhost:8000/${data.FotoUrl}`); // Asumiendo que la API devuelve la URL de la foto
          }
        } else {
          alert('Error al cargar los datos del cliente');
        }
      } catch (error) {
        alert('Error al conectar con el servidor');
      }
    };

    cargarDatosCliente();
  }, [clienteId]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'Foto') {
      const file = files[0];
      setFormData({ ...formData, Foto: file });
      setPreview(URL.createObjectURL(file));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key] === null ? '' : formData[key]); // Append todos los campos
    }

    try {
      const response = await fetch(`http://localhost:8000/personas/${clienteId}`, { // Usamos el ID para la actualización
        method: 'PUT', // O 'PATCH', dependiendo de tu API
        body: data, // Enviamos FormData
      });

      const result = await response.json();
      if (response.ok) {
        alert('Perfil actualizado correctamente');
        // Redirigir a la página de perfil o mi cuenta
        navigate('/micuentacliente');
      } else {
        alert('Error al actualizar el perfil: ' + (result.error || ''));
      }
    } catch (err) {
      console.error(err);
      alert('Error al conectar con el servidor');
    }
  };

  return (
    <div className="comprar-container">
      <nav className="menu-cliente">
        <div className="logo">
          <h2>S´ FOR YOUR NUTRITION</h2>
        </div>
        <ul className="menu-links">
          <li><Link to="/comprar">Ordenar</Link></li>
          <li><Link to="/carrito">Carrito</Link></li>
          <li><Link to="/micuentacliente">Mi Cuenta</Link></li>
        </ul>
      </nav>

      <div className="registro-container">
        <h2>Editar Perfil</h2>
        <form onSubmit={handleSubmit} className="registro-form" encType="multipart/form-data">
          <div className="foto-preview">
            {preview ? (
              <img src={preview} alt="Foto de perfil" style={{ maxWidth: '100px', maxHeight: '100px' }} />
            ) : (
              <div className="placeholder">Foto</div>
            )}
            <input type="file" name="Foto" accept="image/*" onChange={handleChange} />
          </div>

          <input type="number" name="Documento" placeholder="Documento" value={formData.Documento} onChange={handleChange} required readOnly /> {/* Opcional: hacer el documento solo lectura */}
          <input type="text" name="Nombres" placeholder="Nombres" value={formData.Nombres} onChange={handleChange} required />
          <input type="text" name="Apellidos" placeholder="Apellidos" value={formData.Apellidos} onChange={handleChange} required />
          <input type="number" name="Telefono" placeholder="Teléfono" value={formData.Telefono} onChange={handleChange} required />
          <input type="email" name="Email" placeholder="Email" value={formData.Email} onChange={handleChange} required />
          <input type="text" name="Direccion" placeholder="Dirección" value={formData.Direccion} onChange={handleChange} required />
          <input type="text" name="Id_ciudad" placeholder="Ciudad" value={formData.Id_ciudad} onChange={handleChange} required />
          <input type="password" name="Contraseña" placeholder="Nueva Contraseña (opcional)" value={formData.Contraseña} onChange={handleChange} /> {/* Contraseña opcional para modificar */}

          <button type="submit">Guardar Cambios</button>
        </form>
      </div>
    </div>
  );
}

export default EditarPerfilCliente;
