import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useNotificaciones } from './NotificacionesContext'; // Si estás usando un contexto global para notificaciones

function PaginaCliente() {
  const { notificaciones } = useNotificaciones(); // Usamos el contexto para acceder a las notificaciones
  const [persona, setPersona] = useState({}); // Inicializamos como objeto vacío

  useEffect(() => {
    // Recuperar los datos de la persona del localStorage
    const storedPersona = JSON.parse(localStorage.getItem('personas'));
    if (storedPersona) {
      setPersona(storedPersona); // Si existe, actualizar el estado con los datos de la persona
    }
  }, []);

  return (
    <div className="pagina-cliente-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S´ FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/comprar">Ordenar</Link></li>
            <li><Link to="/carrito">Carrito</Link></li>
            <li><Link to="/micuentacliente">Mi Cuenta</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-cliente">
        {/* Mensaje de bienvenida */}
        <h2>Bienvenido nuevamente, {persona?.Nombres || 'Cliente'} 🥤</h2>

        {/* Mostrar las notificaciones si existen */}
        <div className="notificaciones">
          <h3>Tus Notificaciones</h3>
          <ul>
            {notificaciones.length > 0 ? (
              notificaciones.map((n, idx) => (
                <li key={idx} className={`notificacion ${n.tipo}`}>
                  <div className="notificacion-contenido">
                    <span className="notificacion-rol">{n.rol}</span>
                    <span className="notificacion-icono">
                      {n.tipo === 'alerta' ? '🚨' : n.tipo === 'promocion' ? '🎁' : 'ℹ️'}
                    </span>
                    <span className="notificacion-mensaje">{n.mensaje}</span>
                    {n.estado && <span className="notificacion-estado">({n.estado})</span>}
                  </div>
                </li>
              ))
            ) : (
              <li>No tienes notificaciones en este momento.</li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default PaginaCliente;
