import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';
import '../style/menu.css'; // Asegúrate de tener este archivo CSS para los estilos

function Menu() {
  const [isOpen, setIsOpen] = useState(false);
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <>
      <div className="hamburger-btn" onClick={toggleMenu}>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
      </div>

      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="menu-header">
          <h1>S' FOR YOUR NUTRITION</h1>
          <h2>Bienvenido nuevamente</h2>
        </div>

        <nav className="side-nav">
          <Link to="/productos" onClick={toggleMenu}>Productos</Link>
          <Link to="/comprar" onClick={toggleMenu}>Ordenar</Link>
          <Link to="/carrito" onClick={toggleMenu}>Carrito</Link>
          <Link to="/mi-cuenta" onClick={toggleMenu}>Mi Cuenta</Link>
        </nav>
      </div>

      {isOpen && <div className="overlay" onClick={toggleMenu} />}
    </>
  );
}

export default Menu;
