import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function AdminPedidos() {
  // Estados para el formulario de registro
  const [nuevoPedido, setNuevoPedido] = useState({
    clienteId: '',
    productos: [{ productoId: '', cantidad: 1, ingredientesAdicionales: [] }],
    estado: 'pendiente',
    fecha: new Date().toISOString().split('T')[0]
  });

  // Estados para la lista de pedidos
  const [pedidos, setPedidos] = useState([]);
  const [clientes, setClientes] = useState([]);
  const [productos, setProductos] = useState([]);
  const [ingredientes, setIngredientes] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [filtro, setFiltro] = useState('todos');

  // Cargar datos iniciales
  useEffect(() => {
    const cargarDatos = async () => {
      try {
        const [clientesRes, productosRes, ingredientesRes, pedidosRes] = await Promise.all([
          fetch('http://localhost/back_your_nutrition/public/clientes').then(res => res.json()),
          fetch('http://localhost/back_your_nutrition/public/productos').then(res => res.json()),
          fetch('http://localhost/back_your_nutrition/public/ingredientes').then(res => res.json()),
          fetch('http://localhost/back_your_nutrition/public/pedidos').then(res => res.json())
        ]);

        setClientes(clientesRes);
        setProductos(productosRes);
        setIngredientes(ingredientesRes);
        setPedidos(pedidosRes);
      } catch (error) {
        console.error('Error cargando datos:', error);
      } finally {
        setCargando(false);
      }
    };

    cargarDatos();
  }, []);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNuevoPedido(prev => ({ ...prev, [name]: value }));
  };

  // Manejar cambios en productos del pedido
  const handleProductoChange = (index, e) => {
    const { name, value } = e.target;
    const nuevosProductos = [...nuevoPedido.productos];
    nuevosProductos[index] = { ...nuevosProductos[index], [name]: value };
    
    setNuevoPedido({ ...nuevoPedido, productos: nuevosProductos });
  };

  // Añadir otro producto al pedido
  const agregarProducto = () => {
    setNuevoPedido({
      ...nuevoPedido,
      productos: [...nuevoPedido.productos, { productoId: '', cantidad: 1, ingredientesAdicionales: [] }]
    });
  };

  // Eliminar producto del pedido
  const eliminarProducto = (index) => {
    const nuevosProductos = nuevoPedido.productos.filter((_, i) => i !== index);
    setNuevoPedido({ ...nuevoPedido, productos: nuevosProductos });
  };

  // Manejar ingredientes adicionales
  const toggleIngrediente = (productIndex, ingredienteId) => {
    const nuevosProductos = [...nuevoPedido.productos];
    const ingredientes = nuevosProductos[productIndex].ingredientesAdicionales;
    
    if (ingredientes.includes(ingredienteId)) {
      nuevosProductos[productIndex].ingredientesAdicionales = ingredientes.filter(id => id !== ingredienteId);
    } else {
      nuevosProductos[productIndex].ingredientesAdicionales = [...ingredientes, ingredienteId];
    }
    
    setNuevoPedido({ ...nuevoPedido, productos: nuevosProductos });
  };

  // Registrar nuevo pedido
  const registrarPedido = async () => {
    try {
      const res = await fetch('http://localhost/back_your_nutrition/public/pedidos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(nuevoPedido)
      });

      const data = await res.json();
      setPedidos([...pedidos, data]);
      
      // Resetear formulario
      setNuevoPedido({
        clienteId: '',
        productos: [{ productoId: '', cantidad: 1, ingredientesAdicionales: [] }],
        estado: 'pendiente',
        fecha: new Date().toISOString().split('T')[0]
      });
    } catch (error) {
      console.error('Error registrando pedido:', error);
    }
  };

  // Actualizar estado de un pedido
  const actualizarEstado = async (pedidoId, nuevoEstado) => {
    try {
      await fetch(`http://localhost/back_your_nutrition/public/pedidos/${pedidoId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ estado: nuevoEstado })
      });

      setPedidos(pedidos.map(p => 
        p.id === pedidoId ? { ...p, estado: nuevoEstado } : p
      ));
    } catch (error) {
      console.error('Error actualizando pedido:', error);
    }
  };

  // Filtrar pedidos por estado
  const pedidosFiltrados = filtro === 'todos' 
    ? pedidos 
    : pedidos.filter(p => p.estado === filtro);

  // Estilo según estado
  const getEstadoStyle = (estado) => {
    switch (estado) {
      case 'pendiente': return 'bg-yellow-100 text-yellow-800 border-l-yellow-500';
      case 'en preparación': return 'bg-blue-100 text-blue-800 border-l-blue-500';
      case 'completado': return 'bg-green-100 text-green-800 border-l-green-500';
      case 'cancelado': return 'bg-red-100 text-red-800 border-l-red-500';
      default: return 'bg-gray-100 text-gray-800 border-l-gray-500';
    }
  };

  if (cargando) return <div className="p-4">Cargando...</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h2 className="text-xl font-bold">S´ FOR YOUR NUTRITION</h2>
            </div>
            <div className="flex space-x-4">
              <Link to="/admin/pedidos" className="px-3 py-2 rounded-md text-sm font-medium bg-gray-200">
                Pedidos
              </Link>
              <Link to="/admin/notificaciones" className="px-3 py-2 rounded-md text-sm font-medium">
                Notificaciones
              </Link>
              <Link to="/admin/inventario" className="px-3 py-2 rounded-md text-sm font-medium">
                Inventario
              </Link>
              <Link to="/admin/micuenta" className="px-3 py-2 rounded-md text-sm font-medium">
                Mi Cuenta
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Sección de registro de nuevos pedidos */}
          <div className="lg:col-span-1 bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-bold mb-4">Registrar Nuevo Pedido</h2>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Cliente</label>
              <select
                name="clienteId"
                value={nuevoPedido.clienteId}
                onChange={handleChange}
                className="w-full p-2 border rounded"
                required
              >
                <option value="">Seleccionar cliente</option>
                {clientes.map(cliente => (
                  <option key={cliente.id} value={cliente.id}>
                    {cliente.nombre} ({cliente.email})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">Productos</label>
              {nuevoPedido.productos.map((producto, index) => (
                <div key={index} className="mb-4 p-3 border rounded">
                  <select
                    name="productoId"
                    value={producto.productoId}
                    onChange={(e) => handleProductoChange(index, e)}
                    className="w-full p-2 border rounded mb-2"
                    required
                  >
                    <option value="">Seleccionar producto</option>
                    {productos.map(prod => (
                      <option key={prod.id} value={prod.id}>
                        {prod.nombre} (${prod.precio})
                      </option>
                    ))}
                  </select>

                  <div className="mb-2">
                    <label className="block text-sm text-gray-600 mb-1">Cantidad</label>
                    <input
                      type="number"
                      name="cantidad"
                      min="1"
                      value={producto.cantidad}
                      onChange={(e) => handleProductoChange(index, e)}
                      className="w-full p-2 border rounded"
                    />
                  </div>

                  <div className="mb-2">
                    <label className="block text-sm text-gray-600 mb-1">Ingredientes Adicionales</label>
                    <div className="flex flex-wrap gap-2">
                      {ingredientes.map(ing => (
                        <label key={ing.id} className="flex items-center space-x-1 text-sm">
                          <input
                            type="checkbox"
                            checked={producto.ingredientesAdicionales.includes(ing.id)}
                            onChange={() => toggleIngrediente(index, ing.id)}
                            className="rounded"
                          />
                          <span>{ing.nombre}</span>
                        </label>
                      ))}
                    </div>
                  </div>

                  {nuevoPedido.productos.length > 1 && (
                    <button
                      type="button"
                      onClick={() => eliminarProducto(index)}
                      className="mt-2 text-red-500 text-sm"
                    >
                      Eliminar producto
                    </button>
                  )}
                </div>
              ))}

              <button
                type="button"
                onClick={agregarProducto}
                className="text-blue-500 text-sm"
              >
                + Añadir otro producto
              </button>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Estado</label>
              <select
                name="estado"
                value={nuevoPedido.estado}
                onChange={handleChange}
                className="w-full p-2 border rounded"
              >
                <option value="pendiente">Pendiente</option>
                <option value="en preparación">En preparación</option>
                <option value="completado">Completado</option>
                <option value="cancelado">Cancelado</option>
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Fecha</label>
              <input
                type="date"
                name="fecha"
                value={nuevoPedido.fecha}
                onChange={handleChange}
                className="w-full p-2 border rounded"
              />
            </div>

            <button
              onClick={registrarPedido}
              className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
            >
              Registrar Pedido
            </button>
          </div>

          {/* Sección de listado de pedidos */}
          <div className="lg:col-span-2">
            <div className="bg-white p-6 rounded-lg shadow">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-bold">Gestión de Pedidos</h2>
                <select
                  value={filtro}
                  onChange={(e) => setFiltro(e.target.value)}
                  className="p-2 border rounded"
                >
                  <option value="todos">Todos los pedidos</option>
                  <option value="pendiente">Pendientes</option>
                  <option value="en preparación">En preparación</option>
                  <option value="completado">Completados</option>
                  <option value="cancelado">Cancelados</option>
                </select>
              </div>

              {pedidosFiltrados.length === 0 ? (
                <p>No hay pedidos {filtro !== 'todos' ? `con estado "${filtro}"` : 'registrados'}.</p>
              ) : (
                <div className="space-y-4">
                  {pedidosFiltrados.map(pedido => (
                    <div 
                      key={pedido.id} 
                      className={`p-4 border rounded-lg ${getEstadoStyle(pedido.estado)} border-l-4`}
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="font-bold">Pedido #{pedido.id}</h3>
                          <p className="text-sm">
                            Cliente: <span className="font-medium">{pedido.clienteNombre}</span>
                          </p>
                          <p className="text-sm">
                            Fecha: <span className="font-medium">
                              {new Date(pedido.fecha).toLocaleDateString()}
                            </span>
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEstadoStyle(pedido.estado)}`}>
                            {pedido.estado}
                          </span>
                        </div>
                      </div>

                      <div className="mt-3">
                        <h4 className="font-medium text-sm mb-1">Productos:</h4>
                        <ul className="space-y-2">
                          {pedido.productos.map((producto, idx) => (
                            <li key={idx} className="text-sm pl-2">
                              {producto.cantidad}x {producto.nombre} (${producto.precioUnitario})
                              {producto.ingredientesAdicionales.length > 0 && (
                                <div className="text-xs text-gray-600 pl-3">
                                  + {producto.ingredientesAdicionales.join(', ')}
                                </div>
                              )}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div className="mt-3 pt-2 border-t flex justify-between items-center">
                        <div className="font-bold">Total: ${pedido.total}</div>
                        <div className="flex space-x-2">
                          {pedido.estado === 'pendiente' && (
                            <>
                              <button 
                                onClick={() => actualizarEstado(pedido.id, 'en preparación')}
                                className="px-3 py-1 bg-blue-500 text-white text-xs rounded hover:bg-blue-600"
                              >
                                Preparar
                              </button>
                              <button 
                                onClick={() => actualizarEstado(pedido.id, 'cancelado')}
                                className="px-3 py-1 bg-red-500 text-white text-xs rounded hover:bg-red-600"
                              >
                                Cancelar
                              </button>
                            </>
                          )}
                          {pedido.estado === 'en preparación' && (
                            <>
                              <button 
                                onClick={() => actualizarEstado(pedido.id, 'completado')}
                                className="px-3 py-1 bg-green-500 text-white text-xs rounded hover:bg-green-600"
                              >
                                Completar
                              </button>
                              <button 
                                onClick={() => actualizarEstado(pedido.id, 'pendiente')}
                                className="px-3 py-1 bg-yellow-500 text-white text-xs rounded hover:bg-yellow-600"
                              >
                                Revertir
                              </button>
                            </>
                          )}
                          {pedido.estado === 'completado' && (
                            <button 
                              onClick={() => actualizarEstado(pedido.id, 'en preparación')}
                              className="px-3 py-1 bg-blue-500 text-white text-xs rounded hover:bg-blue-600"
                            >
                              Reabrir
                            </button>
                          )}
                          {pedido.estado === 'cancelado' && (
                            <button 
                              onClick={() => actualizarEstado(pedido.id, 'pendiente')}
                              className="px-3 py-1 bg-yellow-500 text-white text-xs rounded hover:bg-yellow-600"
                            >
                              Reactivar
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminPedidos;
