import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState({
    Nombre: '',
    Precio: '',
    Disponibilidad: 1,
    Descripcion: '',
    Tama침o: '',
    Calorias: '',
    Cantidad: 1
  });
  const [editando, setEditando] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error('Error al cargar productos');
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Error al cargar productos', err);
      setError('No se pudieron cargar los productos');
    }
  };

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    // Convertir a n칰mero si es Precio, Cantidad o Calorias
    const valorFinal = ["Precio", "Cantidad", "Calorias"].includes(name) 
      ? value === "" ? "" : Number(value)
      : value;
    
    setNuevo((prev) => ({ ...prev, [name]: valorFinal }));
    setError('');
  };

  const validarFormulario = () => {
    if (!nuevo.Nombre.trim()) {
      setError('El nombre del producto es requerido');
      return false;
    }
    if (!nuevo.Precio || isNaN(nuevo.Precio) || nuevo.Precio <= 0) {
      setError('El precio debe ser un n칰mero mayor a 0');
      return false;
    }
    if (!nuevo.Cantidad || isNaN(nuevo.Cantidad) || nuevo.Cantidad <= 0) {
      setError('La cantidad debe ser un n칰mero mayor a 0');
      return false;
    }
    if (!nuevo.Tama침o) {
      setError('Seleccione un tama침o');
      return false;
    }
    return true;
  };

  const agregar = async () => {
    if (!validarFormulario()) return;

    try {
      // Verificar si el producto ya existe
      const productoExistente = items.find(item => 
        item.Nombre.toLowerCase() === nuevo.Nombre.toLowerCase() &&
        item.Tama침o === nuevo.Tama침o
      );

      if (productoExistente) {
        // Actualizar cantidad del producto existente
        const response = await fetch(`http://localhost:8000/productos/${productoExistente.id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ 
            Cantidad: parseInt(productoExistente.Cantidad) + parseInt(nuevo.Cantidad)
          }),
        });

        if (!response.ok) throw new Error('Error al actualizar cantidad');

        const data = await response.json();
        setItems(items.map(item => 
          item.id === productoExistente.id ? data : item
        ));
        alert(`Se agregaron ${nuevo.Cantidad} unidades a ${nuevo.Nombre}`);
      } else {
        // Crear nuevo producto
        const response = await fetch('http://localhost:8000/productos', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...nuevo,
            Precio: parseFloat(nuevo.Precio),
            Cantidad: parseInt(nuevo.Cantidad),
            Calorias: nuevo.Calorias ? parseInt(nuevo.Calorias) : null
          }),
        });

        if (!response.ok) throw new Error('Error al crear producto');

        const data = await response.json();
        setItems([...items, data]);
        alert('Producto creado exitosamente');
      }

      // Resetear formulario
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: 1,
        Descripcion: '',
        Tama침o: '',
        Calorias: '',
        Cantidad: 1
      });
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al procesar la solicitud');
    }
  };

  const iniciarEdicion = (item) => {
    setEditando(item);
    setNuevo({
      Nombre: item.Nombre,
      Precio: item.Precio,
      Disponibilidad: item.Disponibilidad,
      Descripcion: item.Descripcion,
      Tama침o: item.Tama침o,
      Calorias: item.Calorias,
      Cantidad: item.Cantidad
    });
  };

  const cancelarEdicion = () => {
    setEditando(null);
    setNuevo({
      Nombre: '',
      Precio: '',
      Disponibilidad: 1,
      Descripcion: '',
      Tama침o: '',
      Calorias: '',
      Cantidad: 1
    });
  };

  const actualizarProducto = async () => {
    if (!validarFormulario()) return;

    try {
      // Convertir valores num칠ricos
      const datosActualizados = {
        ...nuevo,
        Precio: parseFloat(nuevo.Precio),
        Cantidad: parseInt(nuevo.Cantidad),
        Calorias: nuevo.Calorias ? parseInt(nuevo.Calorias) : null,
        Disponibilidad: parseInt(nuevo.Disponibilidad)
      };

      const response = await fetch(`http://localhost:8000/productos/${editando.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datosActualizados),
      });

      if (!response.ok) throw new Error('Error al actualizar producto');

      const data = await response.json();
      setItems(items.map(item => item.id === editando.id ? data : item));
      
      setEditando(null);
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: 1,
        Descripcion: '',
        Tama침o: '',
        Calorias: '',
        Cantidad: 1
      });
      alert('Producto actualizado exitosamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al actualizar el producto');
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm('쮼st치s seguro de eliminar este producto?')) return;

    try {
      const response = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Error al eliminar producto');

      setItems(items.filter(item => item.id !== id));
      
      if (editando && editando.id === id) {
        setEditando(null);
        setNuevo({
          Nombre: '',
          Precio: '',
          Disponibilidad: 1,
          Descripcion: '',
          Tama침o: '',
          Calorias: '',
          Cantidad: 1
        });
      }
      alert('Producto eliminado exitosamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error al eliminar el producto');
    }
  };

  return (
    <div>
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S췂 FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin游볷</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        {error && <div className="error-message">{error}</div>}

        <div className="formulario">
          <input
            name="Nombre"
            type="text"
            placeholder="Nombre del producto"
            value={nuevo.Nombre}
            onChange={manejarCambio}
          />

          <input
            name="Precio"
            type="number"
            placeholder="Precio"
            min="0.01"
            step="0.01"
            value={nuevo.Precio}
            onChange={manejarCambio}
          />

          <input
            name="Cantidad"
            type="number"
            placeholder="Cantidad"
            min="1"
            value={nuevo.Cantidad}
            onChange={manejarCambio}
          />

          <input
            name="Descripcion"
            placeholder="Descripci칩n"
            value={nuevo.Descripcion}
            onChange={manejarCambio}
          />

          <select
            name="Tama침o"
            value={nuevo.Tama침o}
            onChange={manejarCambio}
          >
            <option value="" disabled>Seleccione un tama침o</option>
            <option value="100">100 ml</option>
            <option value="150">150 ml</option>
            <option value="250">250 ml</option>
          </select>

          <input
            name="Calorias"
            type="number"
            placeholder="Calor칤as"
            min="0"
            value={nuevo.Calorias}
            onChange={manejarCambio}
          />

          <select
            name="Disponibilidad"
            value={nuevo.Disponibilidad}
            onChange={manejarCambio}
          >
            <option value="1">Disponible</option>
            <option value="0">No disponible</option>
          </select>

          {editando ? (
            <div className="acciones-edicion">
              <button onClick={actualizarProducto}>Guardar Cambios</button>
              <button onClick={cancelarEdicion} className="cancelar">Cancelar</button>
            </div>
          ) : (
            <button onClick={agregar}>Agregar Producto</button>
          )}
        </div>

        <div className="tabla-container">
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Precio</th>
                <th>Descripci칩n</th>
                <th>Tama침o</th>
                <th>Calor칤as</th>
                <th>Cantidad</th>
                <th>Disponible</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {items.length === 0 ? (
                <tr>
                  <td colSpan="8" className="no-items">No hay productos registrados</td>
                </tr>
              ) : (
                items.map((item) => (
                  <tr key={item.id}>
                    <td>{item.Nombre}</td>
                    <td>${parseFloat(item.Precio).toFixed(2)}</td>
                    <td>{item.Descripcion}</td>
                    <td>{item.Tama침o} ml</td>
                    <td>{item.Calorias}</td>
                    <td>{item.Cantidad}</td>
                    <td>{item.Disponibilidad === 1 ? 'S칤' : 'No'}</td>
                    <td className="acciones">
                      <button onClick={() => iniciarEdicion(item)} className="editar">
                        Editar
                      </button>
                      <button onClick={() => eliminarProducto(item.id)} className="eliminar">
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Inventario;
