import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState({
    Nombre: '',
    Precio: '',
    Disponibilidad: '1',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Cantidad: '1'
  });
  const [editando, setEditando] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  // Cargar productos al inicio
  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const res = await fetch('http://localhost:8000/productos');
      
      if (!res.ok) {
        throw new Error(`Error HTTP: ${res.status}`);
      }
      
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Error al cargar productos:', err);
      setError(`Error al cargar productos: ${err.message}`);
    } finally {
      setCargando(false);
    }
  };

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNuevo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validarFormulario = () => {
    const errores = [];
    
    if (!nuevo.Nombre.trim()) {
      errores.push('El nombre es requerido');
    }
    
    if (!nuevo.Precio || isNaN(nuevo.Precio) || parseFloat(nuevo.Precio) <= 0) {
      errores.push('Precio inválido');
    }
    
    if (!nuevo.Cantidad || isNaN(nuevo.Cantidad) || parseInt(nuevo.Cantidad) < 0) {
      errores.push('Cantidad inválida');
    }
    
    if (!nuevo.Tamaño) {
      errores.push('Seleccione un tamaño');
    }
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    
    return true;
  };

  const iniciarEdicion = (producto) => {
    setEditando(producto);
    setNuevo({
      Nombre: producto.Nombre || '',
      Precio: producto.Precio?.toString() || '',
      Disponibilidad: producto.Disponibilidad?.toString() || '1',
      Descripcion: producto.Descripcion || '',
      Tamaño: producto.Tamaño || '',
      Calorias: producto.Calorias?.toString() || '',
      Cantidad: producto.Cantidad?.toString() || '1'
    });
  };

  const cancelarEdicion = () => {
    setEditando(null);
    setNuevo({
      Nombre: '',
      Precio: '',
      Disponibilidad: '1',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Cantidad: '1'
    });
    setError('');
  };

  const actualizarProducto = async () => {
    if (!validarFormulario()) return;
    
    try {
      const datosActualizados = {
        Nombre: nuevo.Nombre,
        Precio: parseFloat(nuevo.Precio),
        Cantidad: parseInt(nuevo.Cantidad),
        Disponibilidad: parseInt(nuevo.Disponibilidad),
        Descripcion: nuevo.Descripcion,
        Tamaño: nuevo.Tamaño,
        Calorias: nuevo.Calorias ? parseInt(nuevo.Calorias) : null
      };

      const response = await fetch(`http://localhost:8000/productos/${editando.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datosActualizados)
      });

      // Manejar posibles errores HTTP
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || 'Error al actualizar');
      }

      const data = await response.json();
      
      // Actualizar el estado local
      setItems(items.map(item => item.id === editando.id ? data : item));
      cancelarEdicion();
      alert('Producto actualizado correctamente');
      
    } catch (error) {
      console.error('Error al actualizar:', error);
      setError(`Error al guardar cambios: ${error.message}`);
    }
  };

  const agregarProducto = async () => {
    if (!validarFormulario()) return;
    
    try {
      const datosProducto = {
        ...nuevo,
        Precio: parseFloat(nuevo.Precio),
        Cantidad: parseInt(nuevo.Cantidad),
        Disponibilidad: parseInt(nuevo.Disponibilidad),
        Calorias: nuevo.Calorias ? parseInt(nuevo.Calorias) : null
      };

      const response = await fetch('http://localhost:8000/productos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datosProducto)
      });

      if (!response.ok) {
        throw new Error('Error al crear producto');
      }

      const data = await response.json();
      setItems([...items, data]);
      
      setNuevo({
        Nombre: '',
        Precio: '',
        Disponibilidad: '1',
        Descripcion: '',
        Tamaño: '',
        Calorias: '',
        Cantidad: '1'
      });
      
      alert('Producto agregado correctamente');
    } catch (error) {
      console.error('Error:', error);
      setError(`Error al agregar producto: ${error.message}`);
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este producto?')) return;
    
    try {
      const response = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Error al eliminar producto');
      }

      setItems(items.filter(item => item.id !== id));
      
      if (editando && editando.id === id) {
        cancelarEdicion();
      }
      
      alert('Producto eliminado correctamente');
    } catch (error) {
      console.error('Error:', error);
      setError(`Error al eliminar: ${error.message}`);
    }
  };

  if (cargando) {
    return <div className="cargando">Cargando productos...</div>;
  }

  return (
    <div className="inventario-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S´ FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin🥤</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        {error && <div className="error-message">{error}</div>}

        <div className="formulario-producto">
          <div className="form-group">
            <label>Nombre</label>
            <input
              name="Nombre"
              value={nuevo.Nombre}
              onChange={manejarCambio}
              placeholder="Nombre del producto"
            />
          </div>

          <div className="form-group">
            <label>Precio ($)</label>
            <input
              name="Precio"
              type="number"
              min="0.01"
              step="0.01"
              value={nuevo.Precio}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Cantidad</label>
            <input
              name="Cantidad"
              type="number"
              min="0"
              value={nuevo.Cantidad}
              onChange={manejarCambio}
            />
          </div>

          <div className="form-group">
            <label>Descripción</label>
            <input
              name="Descripcion"
              value={nuevo.Descripcion}
              onChange={manejarCambio}
              placeholder="Descripción del producto"
            />
          </div>

          <div className="form-group">
            <label>Tamaño (ml)</label>
            <select
              name="Tamaño"
              value={nuevo.Tamaño}
              onChange={manejarCambio}
            >
              <option value="">Seleccione...</option>
              <option value="100">100 ml</option>
              <option value="150">150 ml</option>
              <option value="250">250 ml</option>
            </select>
          </div>

          <div className="form-group">
            <label>Calorías</label>
            <input
              name="Calorias"
              type="number"
              min="0"
              value={nuevo.Calorias}
              onChange={manejarCambio}
              placeholder="Calorías"
            />
          </div>

          <div className="form-group">
            <label>Disponibilidad</label>
            <select
              name="Disponibilidad"
              value={nuevo.Disponibilidad}
              onChange={manejarCambio}
            >
              <option value="1">Disponible</option>
              <option value="0">No disponible</option>
            </select>
          </div>

          <div className="acciones-formulario">
            {editando ? (
              <>
                <button onClick={actualizarProducto} className="btn-guardar">
                  Guardar Cambios
                </button>
                <button onClick={cancelarEdicion} className="btn-cancelar">
                  Cancelar
                </button>
              </>
            ) : (
              <button onClick={agregarProducto} className="btn-agregar">
                Agregar Producto
              </button>
            )}
          </div>
        </div>

        <div className="tabla-productos">
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Precio</th>
                <th>Descripción</th>
                <th>Tamaño</th>
                <th>Calorías</th>
                <th>Cantidad</th>
                <th>Disponible</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {items.length === 0 ? (
                <tr>
                  <td colSpan="8">No hay productos registrados</td>
                </tr>
              ) : (
                items.map((item) => (
                  <tr key={item.id}>
                    <td>{item.Nombre}</td>
                    <td>${item.Precio?.toFixed(2)}</td>
                    <td>{item.Descripcion}</td>
                    <td>{item.Tamaño} ml</td>
                    <td>{item.Calorias || '-'}</td>
                    <td>{item.Cantidad}</td>
                    <td>
                      <span className={`disponibilidad ${item.Disponibilidad ? 'disponible' : 'no-disponible'}`}>
                        {item.Disponibilidad ? 'Sí' : 'No'}
                      </span>
                    </td>
                    <td className="acciones">
                      <button 
                        onClick={() => iniciarEdicion(item)} 
                        className="btn-editar"
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => eliminarProducto(item.id)} 
                        className="btn-eliminar"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Inventario;
