import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function PaginaBatidos() {
  const navigate = useNavigate();
  const [batidos, setBatidos] = useState([]);
  const [pedidosPendientes, setPedidosPendientes] = useState([]);
  const [inventario, setInventario] = useState([]);
  const [loading, setLoading] = useState(true);

  // Cargar datos iniciales
  useEffect(() => {
    const cargarDatos = async () => {
      try {
        // Verificar autenticación
        const isAuthenticated = localStorage.getItem('batidosAuth') === 'true';
        if (!isAuthenticated) {
          navigate('/loginbatidos');
          return;
        }

        // Cargar batidos desde el inventario
        const invResponse = await fetch('http://localhost:8000/productos');
        const invData = await invResponse.json();
        setInventario(invData);
        
        // Filtrar solo los batidos (puedes ajustar este filtro según tu estructura de datos)
        const batidosData = invData.filter(item => 
          item.Nombre.toLowerCase().includes('batido') || 
          item.Nombre.toLowerCase().includes('malteada')
        );
        setBatidos(batidosData);

        // Cargar pedidos pendientes
        const pedResponse = await fetch('http://localhost:8000/pedidos');
        const pedData = await pedResponse.json();
        const pendientes = pedData.filter(pedido => 
          pedido.estado === 'pendiente' || 
          pedido.estado === 'en preparación'
        );
        setPedidosPendientes(pendientes);

      } catch (error) {
        console.error('Error cargando datos:', error);
      } finally {
        setLoading(false);
      }
    };

    cargarDatos();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('batidosAuth');
    localStorage.removeItem('clienteDocumento');
    navigate('/loginbatidos');
  };

  const actualizarEstadoPedido = async (pedidoId, nuevoEstado) => {
    try {
      const response = await fetch(`http://localhost:8000/pedidos/${pedidoId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ estado: nuevoEstado }),
      });

      if (!response.ok) throw new Error('Error actualizando pedido');

      // Actualizar lista de pedidos
      setPedidosPendientes(prev => 
        prev.map(ped => 
          ped.id === pedidoId ? { ...ped, estado: nuevoEstado } : ped
        )
      );
    } catch (error) {
      console.error('Error:', error);
      alert('No se pudo actualizar el pedido');
    }
  };

  const actualizarDisponibilidad = async (productoId, disponibilidad) => {
    try {
      const response = await fetch(`http://localhost:8000/productos/${productoId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Disponibilidad: disponibilidad ? 1 : 0 }),
      });

      if (!response.ok) throw new Error('Error actualizando producto');

      // Actualizar lista de inventario
      setInventario(prev => 
        prev.map(item => 
          item.id === productoId ? { ...item, Disponibilidad: disponibilidad ? 1 : 0 } : item
        )
      );
      
      // Actualizar lista de batidos si es necesario
      setBatidos(prev => 
        prev.map(item => 
          item.id === productoId ? { ...item, Disponibilidad: disponibilidad ? 1 : 0 } : item
        )
      );
    } catch (error) {
      console.error('Error:', error);
      alert('No se pudo actualizar la disponibilidad');
    }
  };

  if (loading) {
    return <div className="loading">Cargando...</div>;
  }

  return (
    <div className="batidos-container">
      <nav className="menu-cliente">
        <div className="logo">
          <h2>S´ FOR YOUR NUTRITION - BATIDOS</h2>
        </div>
        <ul className="menu-links">
          <li><Link to="/pedidos">Pedidos</Link></li>
          <li><Link to="/inventario">Inventario</Link></li>
          <li><button onClick={handleLogout} className="logout-btn">Cerrar Sesión</button></li>
        </ul>
      </nav>

      <div className="contenido-principal">
        <div className="seccion-pedidos">
          <h2>Pedidos Pendientes</h2>
          {pedidosPendientes.length === 0 ? (
            <p>No hay pedidos pendientes</p>
          ) : (
            <div className="lista-pedidos">
              {pedidosPendientes.map(pedido => (
                <div key={pedido.id} className="pedido-card">
                  <h3>Pedido #{pedido.id}</h3>
                  <p><strong>Cliente:</strong> {pedido.cliente}</p>
                  <p><strong>Estado:</strong> {pedido.estado}</p>
                  
                  <div className="productos-pedido">
                    {pedido.productos.map((producto, index) => (
                      <div key={index} className="producto-item">
                        <p>{producto.Nombre} x {producto.cantidad}</p>
                        {producto.ingredientesAdicionales?.length > 0 && (
                          <ul>
                            {producto.ingredientesAdicionales.map((ing, i) => (
                              <li key={i}>+ {ing}</li>
                            ))}
                          </ul>
                        )}
                      </div>
                    ))}
                  </div>
                  
                  <div className="acciones-pedido">
                    {pedido.estado === 'pendiente' && (
                      <button 
                        onClick={() => actualizarEstadoPedido(pedido.id, 'en preparación')}
                        className="btn-preparacion"
                      >
                        Iniciar Preparación
                      </button>
                    )}
                    {pedido.estado === 'en preparación' && (
                      <button 
                        onClick={() => actualizarEstadoPedido(pedido.id, 'listo para entrega')}
                        className="btn-listo"
                      >
                        Marcar como Listo
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="seccion-batidos">
          <h2>Batidos Disponibles</h2>
          <div className="lista-batidos">
            {batidos.map(batido => (
               <div key={batido.id} className="batido-card">
                <h3>{batido.Nombre}</h3>
                <p>{batido.Descripcion}</p>
                <p className="precio">${batido.Precio?.toFixed(2)}</p>
                <p className="disponibilidad">
                  Disponible: 
                  <label className="switch">
                    <input 
                      type="checkbox" 
                      checked={batido.Disponibilidad === 1}
                      onChange={(e) => actualizarDisponibilidad(batido.id, e.target.checked)}
                    />
                    <span className="slider round"></span>
                  </label>
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="seccion-inventario">
          <h2>Inventario Rápido</h2>
          <div className="resumen-inventario">
            <p>Total productos: {inventario.length}</p>
            <p>Productos disponibles: {inventario.filter(item => item.Disponibilidad === 1).length}</p>
            <Link to="/inventario" className="btn-ver-inventario">
              Ver Inventario Completo
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PaginaBatidos;
