import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/notificaciones.css';

function Notificaciones() {
  const location = useLocation();
  const isAdmin = location.pathname.includes('admin');
  const [nueva, setNueva] = useState({
    id: Date.now(),
    rol: isAdmin ? 'Preparador de batidos' : 'Sistema',
    mensaje: '',
    tipo: 'info',
    estado: 'preparando',
    destinatario: isAdmin ? 'admin' : 'cliente'
  });

  const [notificaciones, setNotificaciones] = useState([]);
  const [editandoId, setEditandoId] = useState(null);

  // Cargar notificaciones desde localStorage
  useEffect(() => {
    const cargarNotificaciones = () => {
      const notificacionesGuardadas = JSON.parse(localStorage.getItem('notificaciones')) || [];
      // Filtrar por tipo de usuario
      const filtradas = notificacionesGuardadas.filter(notif => 
        isAdmin ? notif.destinatario === 'admin' : notif.destinatario === 'cliente'
      );
      setNotificaciones(filtradas);
    };

    cargarNotificaciones();
    window.addEventListener('storage', cargarNotificaciones);

    return () => {
      window.removeEventListener('storage', cargarNotificaciones);
    };
  }, [isAdmin]);

  // Efecto para notificaci贸n de bienvenida solo para clientes
  useEffect(() => {
    if (!isAdmin && notificaciones.length === 0) {
      const notificacionBienvenida = {
        id: Date.now(),
        rol: 'Sistema',
        mensaje: '隆Bienvenido a S麓 FOR YOUR NUTRITION! 驴Listo para ordenar?',
        tipo: 'bienvenida',
        destinatario: 'cliente',
        accion: {
          texto: 'Ver Productos',
          ruta: '/comprar'
        }
      };
      agregarNotificacion(notificacionBienvenida);
    }
  }, [isAdmin, notificaciones.length]);

  const manejarCambio = (e) => {
    const { name, value } = e.target;
    setNueva(prev => ({ ...prev, [name]: value }));
  };

  const agregarNotificacion = (notificacion) => {
    const notificacionesActuales = JSON.parse(localStorage.getItem('notificaciones')) || [];
    const nuevasNotificaciones = [...notificacionesActuales, notificacion];
    localStorage.setItem('notificaciones', JSON.stringify(nuevasNotificaciones));
    setNotificaciones(prev => [...prev, notificacion]);
    return nuevasNotificaciones;
  };

  const actualizarNotificacion = (id, datosActualizados) => {
    const notificacionesActuales = JSON.parse(localStorage.getItem('notificaciones')) || [];
    const nuevasNotificaciones = notificacionesActuales.map(notif => 
      notif.id === id ? { ...notif, ...datosActualizados } : notif
    );
    localStorage.setItem('notificaciones', JSON.stringify(nuevasNotificaciones));
    setNotificaciones(prev => 
      prev.map(notif => notif.id === id ? { ...notif, ...datosActualizados } : notif)
    );
    return nuevasNotificaciones;
  };

  const eliminarNotificacion = (id) => {
    const notificacionesActuales = JSON.parse(localStorage.getItem('notificaciones')) || [];
    const nuevasNotificaciones = notificacionesActuales.filter(notif => notif.id !== id);
    localStorage.setItem('notificaciones', JSON.stringify(nuevasNotificaciones));
    setNotificaciones(prev => prev.filter(notif => notif.id !== id));
    return nuevasNotificaciones;
  };

  const registrarNotificacion = () => {
    if (nueva.mensaje.trim()) {
      const notifCompleta = {
        ...nueva,
        id: Date.now(),
        fecha: new Date().toISOString()
      };
      
      agregarNotificacion(notifCompleta);
      setNueva({
        id: Date.now(),
        rol: isAdmin ? 'Preparador de batidos' : 'Sistema',
        mensaje: '',
        tipo: 'info',
        estado: 'preparando',
        destinatario: isAdmin ? 'admin' : 'cliente'
      });
    }
  };

  const iniciarEdicion = (notificacion) => {
    setNueva(notificacion);
    setEditandoId(notificacion.id);
  };

  const guardarEdicion = () => {
    if (nueva.mensaje.trim()) {
      actualizarNotificacion(editandoId, {
        rol: nueva.rol,
        mensaje: nueva.mensaje,
        tipo: nueva.tipo,
        estado: nueva.estado,
        destinatario: nueva.destinatario,
        fecha: new Date().toISOString()
      });
      setEditandoId(null);
      setNueva({
        id: Date.now(),
        rol: isAdmin ? 'Preparador de batidos' : 'Sistema',
        mensaje: '',
        tipo: 'info',
        estado: 'preparando',
        destinatario: isAdmin ? 'admin' : 'cliente'
      });
    }
  };

  const cambiarEstadoPedido = (idPedido, nuevoEstado) => {
    const notificacionActualizada = {
      estado: nuevoEstado,
      mensaje: `Pedido #${idPedido} est谩 ${nuevoEstado === 'listo' ? 'listo para recoger' : 'en preparaci贸n'}`
    };
    actualizarNotificacion(idPedido, notificacionActualizada);
  };

  return (
    <div className="notificaciones-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S麓 FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            {isAdmin && <li><Link to="/inventario">Inventario</Link></li>}
            <li><Link to={isAdmin ? "/micuentaadmin" : "/mi-cuenta"}>Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-notificaciones">
        <h1 className="titulo-bienvenida">
          {isAdmin ? 'Centro de Notificaciones イ' : 'Tus Notificaciones '}
        </h1>

        {isAdmin && (
          <div className="panel-creacion">
            <h2>{editandoId ? 'Editar Notificaci贸n' : 'Crear Nueva Notificaci贸n'}</h2>
            <div className="formulario-notificacion">
              <div className="form-group">
                <label>Rol:</label>
                <select name="rol" value={nueva.rol} onChange={manejarCambio}>
                  <option value="Preparador de batidos">Preparador de batidos</option>
                  <option value="Empleado">Empleado</option>
                  <option value="Sistema">Sistema</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Tipo:</label>
                <select name="tipo" value={nueva.tipo} onChange={manejarCambio}>
                  <option value="info">Informaci贸n</option>
                  <option value="alerta">Alerta</option>
                  <option value="promocion">Promoci贸n</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Destinatario:</label>
                <select name="destinatario" value={nueva.destinatario} onChange={manejarCambio}>
                  <option value="admin">Administrador</option>
                  <option value="cliente">Cliente</option>
                  <option value="todos">Todos</option>
                </select>
              </div>
              
              {nueva.rol === 'Preparador de batidos' && (
                <div className="form-group">
                  <label>Estado:</label>
                  <select name="estado" value={nueva.estado} onChange={manejarCambio}>
                    <option value="preparando">Preparando</option>
                    <option value="listo">Listo</option>
                  </select>
                </div>
              )}
              
              <div className="form-group">
                <label>Mensaje:</label>
                <input
                  type="text"
                  name="mensaje"
                  placeholder="Escribe tu mensaje..."
                  value={nueva.mensaje}
                  onChange={manejarCambio}
                />
              </div>
              
              <div className="acciones-formulario">
                {editandoId ? (
                  <>
                    <button onClick={guardarEdicion} className="btn-guardar">
                      Guardar Cambios
                    </button>
                    <button 
                      onClick={() => {
                        setEditandoId(null);
                        setNueva({
                          id: Date.now(),
                          rol: 'Preparador de batidos',
                          mensaje: '',
                          tipo: 'info',
                          estado: 'preparando',
                          destinatario: isAdmin ? 'admin' : 'cliente'
                        });
                      }} 
                      className="btn-cancelar"
                    >
                      Cancelar
                    </button>
                  </>
                ) : (
                  <button onClick={registrarNotificacion} className="btn-registrar">
                    Registrar Notificaci贸n
                  </button>
                )}
              </div>
            </div>
          </div>
        )}

        <div className="lista-notificaciones">
          <h2>{isAdmin ? 'Historial de Notificaciones' : 'Tus Alertas Recientes'}</h2>
          
          {notificaciones.length === 0 ? (
            <p className="sin-notificaciones">
              {isAdmin ? 'No hay notificaciones registradas' : 'No tienes notificaciones nuevas'}
            </p>
          ) : (
            <ul>
              {notificaciones
                .sort((a, b) => new Date(b.fecha) - new Date(a.fecha))
                .map((notif) => (
                  <li key={notif.id} className={`notificacion ${notif.tipo}`}>
                    <div className="notificacion-contenido">
                      <span className="notificacion-fecha">
                        {new Date(notif.fecha).toLocaleString()}
                      </span>
                      <span className="notificacion-rol">{notif.rol}</span>
                      <span className="notificacion-icono">
                        {notif.tipo === 'alerta' ? '' : 
                         notif.tipo === 'promocion' ? '' : 
                         notif.tipo === 'bienvenida' ? '' : '癸'}
                      </span>
                      <span className="notificacion-mensaje">{notif.mensaje}</span>
                      {notif.estado && (
                        <span className="notificacion-estado">
                          ({notif.estado})
                        </span>
                      )}
                    </div>
                    
                    <div className="notificacion-acciones">
                      {notif.accion && (
                        <Link to={notif.accion.ruta} className="notificacion-accion">
                          {notif.accion.texto} 
                        </Link>
                      )}
                      
                      {isAdmin && (
                        <>
                          <button 
                            onClick={() => iniciarEdicion(notif)}
                            className="btn-editar-notif"
                          >
                            Editar
                          </button>
                          <button 
                            onClick={() => eliminarNotificacion(notif.id)}
                            className="btn-eliminar-notif"
                          >
                            Eliminar
                          </button>
                        </>
                      )}
                    </div>
                  </li>
                ))
              }
            </ul>
          )}
        </div>

        {!isAdmin && (
          <div className="call-to-action">
            <h3>驴Qu茅 te gustar铆a ordenar hoy?</h3>
            <p>Descubre nuestros deliciosos batidos y productos saludables</p>
            <Link to="/comprar" className="btn-ordenar">
              Ver Cat谩logo Completo
            </Link>
          </div>
        )}
      </div>
    </div>
  );
}

export default Notificaciones;
