import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const [isOpen, setIsOpen] = useState(false);
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <div className="app-container">
      {/* Menú Hamburguesa */}
      <div className="menu-toggle" onClick={toggleMenu}>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
      </div>

      {/* Menú Lateral */}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="brand-header">
          <h1>S' FOR YOUR NUTRITION</h1>
          <h2>Bienvenido nuevamente</h2>
        </div>

        <nav className="main-nav">
          <Link to="/productos" onClick={toggleMenu} className="nav-item">Productos</Link>
          <Link to="/ordenar" onClick={toggleMenu} className="nav-item">Ordenar</Link>
          <Link to="/carrito" onClick={toggleMenu} className="nav-item">Carrito</Link>
          <Link to="/mi-cuenta" onClick={toggleMenu} className="nav-item">Mi Cuenta</Link>
        </nav>
      </div>

      {/* Overlay */}
      {isOpen && <div className="overlay" onClick={toggleMenu} />}
    </div>
  );
}

export default Menu;
