import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function AdminPedidos() {
  const [pedidos, setPedidos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [filtro, setFiltro] = useState('todos');
  const [pedidoEditando, setPedidoEditando] = useState(null);
  const [productosDisponibles, setProductosDisponibles] = useState([]);
  const [ingredientesDisponibles, setIngredientesDisponibles] = useState([]);

  // Cargar pedidos y datos relacionados
  useEffect(() => {
    const cargarDatos = async () => {
      try {
        const [pedidosRes, productosRes, ingredientesRes] = await Promise.all([
          fetch('http://localhost/back_your_nutrition/public/pedidos').then(res => res.json()),
          fetch('http://localhost/back_your_nutrition/public/productos').then(res => res.json()),
          fetch('http://localhost/back_your_nutrition/public/ingredientes').then(res => res.json())
        ]);

        setPedidos(pedidosRes);
        setProductosDisponibles(productosRes);
        setIngredientesDisponibles(ingredientesRes);
      } catch (error) {
        console.error('Error cargando datos:', error);
      } finally {
        setCargando(false);
      }
    };

    cargarDatos();
  }, []);

  // Filtrar pedidos por estado
  const pedidosFiltrados = filtro === 'todos' 
    ? pedidos 
    : pedidos.filter(p => p.estado === filtro);

  // Estilo según estado
  const getEstadoStyle = (estado) => {
    switch (estado) {
      case 'pendiente': return 'bg-yellow-100 text-yellow-800 border-l-yellow-500';
      case 'en preparación': return 'bg-blue-100 text-blue-800 border-l-blue-500';
      case 'completado': return 'bg-green-100 text-green-800 border-l-green-500';
      case 'cancelado': return 'bg-red-100 text-red-800 border-l-red-500';
      default: return 'bg-gray-100 text-gray-800 border-l-gray-500';
    }
  };

  // Actualizar estado del pedido
  const actualizarEstadoPedido = async (pedidoId, nuevoEstado) => {
    try {
      const response = await fetch(`http://localhost/back_your_nutrition/public/pedidos/${pedidoId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ estado: nuevoEstado })
      });

      const pedidoActualizado = await response.json();

      setPedidos(pedidos.map(p => 
        p.id === pedidoId ? pedidoActualizado : p
      ));
    } catch (error) {
      console.error('Error actualizando estado:', error);
    }
  };

  // Iniciar edición de pedido
  const iniciarEdicion = (pedido) => {
    setPedidoEditando({ ...pedido });
  };

  // Guardar cambios en el pedido
  const guardarCambios = async () => {
    try {
      const response = await fetch(`http://localhost/back_your_nutrition/public/pedidos/${pedidoEditando.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(pedidoEditando)
      });

      const pedidoActualizado = await response.json();

      setPedidos(pedidos.map(p => 
        p.id === pedidoActualizado.id ? pedidoActualizado : p
      ));
      setPedidoEditando(null);
    } catch (error) {
      console.error('Error actualizando pedido:', error);
    }
  };

  // Manejar cambios en la edición
  const handleEdicionChange = (e) => {
    const { name, value } = e.target;
    setPedidoEditando(prev => ({ ...prev, [name]: value }));
  };

  // Manejar cambios en productos editados
  const handleProductoEditChange = (productoIndex, e) => {
    const { name, value } = e.target;
    const nuevosProductos = [...pedidoEditando.productos];
    nuevosProductos[productoIndex] = { ...nuevosProductos[productoIndex], [name]: value };
    setPedidoEditando({ ...pedidoEditando, productos: nuevosProductos });
  };

  // Toggle ingrediente adicional
  const toggleIngrediente = (productoIndex, ingredienteId) => {
    const nuevosProductos = [...pedidoEditando.productos];
    const ingredientes = nuevosProductos[productoIndex].ingredientesAdicionales;
    
    nuevosProductos[productoIndex].ingredientesAdicionales = ingredientes.includes(ingredienteId)
      ? ingredientes.filter(id => id !== ingredienteId)
      : [...ingredientes, ingredienteId];
    
    setPedidoEditando({ ...pedidoEditando, productos: nuevosProductos });
  };

  if (cargando) return <div className="p-4">Cargando pedidos...</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h2 className="text-xl font-bold">S´ FOR YOUR NUTRITION</h2>
            </div>
            <div className="flex space-x-4">
              <Link to="/admin/pedidos" className="px-3 py-2 rounded-md text-sm font-medium bg-gray-200">
                Pedidos
              </Link>
              <Link to="/admin/notificaciones" className="px-3 py-2 rounded-md text-sm font-medium">
                Notificaciones
              </Link>
              <Link to="/admin/inventario" className="px-3 py-2 rounded-md text-sm font-medium">
                Inventario
              </Link>
              <Link to="/admin/micuenta" className="px-3 py-2 rounded-md text-sm font-medium">
                Mi Cuenta
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold">Gestión de Pedidos</h2>
            <select
              value={filtro}
              onChange={(e) => setFiltro(e.target.value)}
              className="p-2 border rounded"
            >
              <option value="todos">Todos los pedidos</option>
              <option value="pendiente">Pendientes</option>
              <option value="en preparación">En preparación</option>
              <option value="completado">Completados</option>
              <option value="cancelado">Cancelados</option>
            </select>
          </div>

          {pedidosFiltrados.length === 0 ? (
            <p>No hay pedidos {filtro !== 'todos' ? `con estado "${filtro}"` : 'registrados'}.</p>
          ) : (
            <div className="space-y-4">
              {pedidosFiltrados.map(pedido => (
                <div key={pedido.id}>
                  {pedidoEditando?.id === pedido.id ? (
                    <div className="p-4 border rounded-lg bg-gray-50">
                      <h3 className="font-bold mb-3">Editando Pedido #{pedido.id}</h3>
                      
                      <div className="mb-3">
                        <label className="block text-sm font-medium mb-1">Estado</label>
                        <select
                          name="estado"
                          value={pedidoEditando.estado}
                          onChange={handleEdicionChange}
                          className="w-full p-2 border rounded"
                        >
                          <option value="pendiente">Pendiente</option>
                          <option value="en preparación">En preparación</option>
                          <option value="completado">Completado</option>
                          <option value="cancelado">Cancelado</option>
                        </select>
                      </div>

                      <div className="mb-3">
                        <label className="block text-sm font-medium mb-1">Productos</label>
                        {pedidoEditando.productos.map((producto, index) => (
                          <div key={index} className="mb-3 p-3 border rounded bg-white">
                            <div className="mb-2">
                              <label className="block text-sm mb-1">Producto</label>
                              <select
                                name="productoId"
                                value={producto.productoId}
                                onChange={(e) => handleProductoEditChange(index, e)}
                                className="w-full p-2 border rounded"
                              >
                                {productosDisponibles.map(prod => (
                                  <option key={prod.id} value={prod.id}>
                                    {prod.nombre} (${prod.precio})
                                  </option>
                                ))}
                              </select>
                            </div>

                            <div className="mb-2">
                              <label className="block text-sm mb-1">Cantidad</label>
                              <input
                                type="number"
                                name="cantidad"
                                min="1"
                                value={producto.cantidad}
                                onChange={(e) => handleProductoEditChange(index, e)}
                                className="w-full p-2 border rounded"
                              />
                            </div>

                            <div className="mb-2">
                              <label className="block text-sm mb-1">Ingredientes Adicionales</label>
                              <div className="flex flex-wrap gap-2">
                                {ingredientesDisponibles.map(ing => (
                                  <label key={ing.id} className="flex items-center space-x-1 text-sm">
                                    <input
                                      type="checkbox"
                                      checked={producto.ingredientesAdicionales.includes(ing.id)}
                                      onChange={() => toggleIngrediente(index, ing.id)}
                                      className="rounded"
                                    />
                                    <span>{ing.nombre}</span>
                                  </label>
                                ))}
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>

                      <div className="flex justify-end space-x-2">
                        <button
                          onClick={() => setPedidoEditando(null)}
                          className="px-4 py-2 border rounded"
                        >
                          Cancelar
                        </button>
                        <button
                          onClick={guardarCambios}
                          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                        >
                          Guardar Cambios
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className={`p-4 border rounded-lg ${getEstadoStyle(pedido.estado)} border-l-4`}>
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="font-bold">Pedido #{pedido.id}</h3>
                          <p className="text-sm">
                            Cliente: <span className="font-medium">{pedido.clienteNombre}</span>
                          </p>
                          <p className="text-sm">
                            Fecha: <span className="font-medium">
                              {new Date(pedido.fecha).toLocaleDateString()}
                            </span>
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEstadoStyle(pedido.estado)}`}>
                            {pedido.estado}
                          </span>
                        </div>
                      </div>

                      <div className="mt-3">
                        <h4 className="font-medium text-sm mb-1">Productos:</h4>
                        <ul className="space-y-2">
                          {pedido.productos.map((producto, idx) => (
                            <li key={idx} className="text-sm pl-2">
                              {producto.cantidad}x {producto.nombre} (${producto.precioUnitario})
                              {producto.ingredientesAdicionales.length > 0 && (
                                <div className="text-xs text-gray-600 pl-3">
                                  + {producto.ingredientesAdicionales.join(', ')}
                                </div>
                              )}
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div className="mt-3 pt-2 border-t flex justify-between items-center">
                        <div className="font-bold">Total: ${pedido.total}</div>
                        <div className="flex space-x-2">
                          <button 
                            onClick={() => iniciarEdicion(pedido)}
                            className="px-3 py-1 bg-gray-500 text-white text-xs rounded hover:bg-gray-600"
                          >
                            Editar
                          </button>
                          
                          {pedido.estado === 'pendiente' && (
                            <>
                              <button 
                                onClick={() => actualizarEstadoPedido(pedido.id, 'en preparación')}
                                className="px-3 py-1 bg-blue-500 text-white text-xs rounded hover:bg-blue-600"
                              >
                                Preparar
                              </button>
                              <button 
                                onClick={() => actualizarEstadoPedido(pedido.id, 'cancelado')}
                                className="px-3 py-1 bg-red-500 text-white text-xs rounded hover:bg-red-600"
                              >
                                Cancelar
                              </button>
                            </>
                          )}
                          {pedido.estado === 'en preparación' && (
                            <>
                              <button 
                                onClick={() => actualizarEstadoPedido(pedido.id, 'completado')}
                                className="px-3 py-1 bg-green-500 text-white text-xs rounded hover:bg-green-600"
                              >
                                Completar
                              </button>
                              <button 
                                onClick={() => actualizarEstadoPedido(pedido.id, 'pendiente')}
                                className="px-3 py-1 bg-yellow-500 text-white text-xs rounded hover:bg-yellow-600"
                              >
                                Revertir
                              </button>
                            </>
                          )}
                          {pedido.estado === 'completado' && (
                            <button 
                              onClick={() => actualizarEstadoPedido(pedido.id, 'en preparación')}
                              className="px-3 py-1 bg-blue-500 text-white text-xs rounded hover:bg-blue-600"
                            >
                              Reabrir
                            </button>
                          )}
                          {pedido.estado === 'cancelado' && (
                            <button 
                              onClick={() => actualizarEstadoPedido(pedido.id, 'pendiente')}
                              className="px-3 py-1 bg-yellow-500 text-white text-xs rounded hover:bg-yellow-600"
                            >
                              Reactivar
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminPedidos;
