import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/register.css';

function AdminPedidos() {
  const [pedidos, setPedidos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);
  const [filtro, setFiltro] = useState('todos');
  const [pedidoAEliminar, setPedidoAEliminar] = useState(null);
  const [mostrarModalEliminar, setMostrarModalEliminar] = useState(false);

  const cargarPedidos = async () => {
    try {
      setCargando(true);
      setError(null);
      
      const response = await fetch('http://localhost/back_your_nutrition/public/pedidos');
      
      if (!response.ok) {
        throw new Error('Error al cargar pedidos');
      }
      
      const data = await response.json();
      setPedidos(data);
    } catch (err) {
      console.error('Error:', err);
      setError(err.message);
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarPedidos();
  }, []);

  const actualizarEstadoPedido = async (pedidoId, nuevoEstado) => {
    try {
      const response = await fetch(
        `http://localhost/back_your_nutrition/public/pedidos/${pedidoId}`, 
        {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ estado: nuevoEstado })
        }
      );

      if (!response.ok) {
        throw new Error('Error al actualizar estado');
      }

      // Actualizar el estado localmente
      setPedidos(pedidos.map(p => 
        p.id === pedidoId ? { ...p, estado: nuevoEstado } : p
      ));
      
      // Recargar los pedidos para asegurar consistencia
      cargarPedidos();
    } catch (err) {
      console.error('Error al actualizar estado:', err);
      setError(err.message);
    }
  };

  const confirmarEliminacion = (pedido) => {
    setPedidoAEliminar(pedido);
    setMostrarModalEliminar(true);
  };

  const eliminarPedido = async () => {
    if (!pedidoAEliminar) return;
    
    try {
      const response = await fetch(
        `http://localhost/back_your_nutrition/public/pedidos/${pedidoAEliminar.id}`, 
        {
          method: 'DELETE'
        }
      );

      if (!response.ok) {
        throw new Error('Error al eliminar pedido');
      }

      // Actualizar la lista de pedidos
      setPedidos(pedidos.filter(p => p.id !== pedidoAEliminar.id));
      setMostrarModalEliminar(false);
      setPedidoAEliminar(null);
    } catch (err) {
      console.error('Error al eliminar pedido:', err);
      setError(err.message);
    }
  };

  const filtrarPedidos = (estado) => {
    if (estado === 'todos') {
      cargarPedidos();
    } else {
      setPedidos(pedidos.filter(p => p.estado === estado));
    }
    setFiltro(estado);
  };

  const getEstadoStyle = (estado) => {
    const estilos = {
      pendiente: 'bg-yellow-100 text-yellow-800 border-l-yellow-500',
      'en preparacion': 'bg-blue-100 text-blue-800 border-l-blue-500',
      completado: 'bg-green-100 text-green-800 border-l-green-500',
      cancelado: 'bg-red-100 text-red-800 border-l-red-500'
    };
    return estilos[estado] || 'bg-gray-100 text-gray-800 border-l-gray-500';
  };

  const formatearFecha = (fecha) => {
    return new Date(fecha).toLocaleDateString('es-ES', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (cargando) return (
    <div className="p-4 text-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
      <p>Cargando pedidos...</p>
    </div>
  );

  if (error) return (
    <div className="p-4 bg-red-100 text-red-700 rounded-lg mx-4">
      <h3 className="font-bold">Error</h3>
      <p>{error}</p>
      <button 
        onClick={cargarPedidos}
        className="mt-2 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
      >
        Reintentar
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h2 className="text-xl font-bold">S´ FOR YOUR NUTRITION</h2>
            </div>
            <div className="flex space-x-4">
              <Link to="/admin/pedidos" className="px-3 py-2 rounded-md text-sm font-medium bg-gray-200">
                Pedidos
              </Link>
              <Link to="/admin/notificaciones" className="px-3 py-2 rounded-md text-sm font-medium">
                Notificaciones
              </Link>
              <Link to="/admin/inventario" className="px-3 py-2 rounded-md text-sm font-medium">
                Inventario
              </Link>
              <Link to="/admin/micuenta" className="px-3 py-2 rounded-md text-sm font-medium">
                Mi Cuenta
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold">Gestión de Pedidos</h2>
            <div className="flex items-center space-x-4">
              <select
                value={filtro}
                onChange={(e) => filtrarPedidos(e.target.value)}
                className="p-2 border rounded"
              >
                <option value="todos">Todos los pedidos</option>
                <option value="pendiente">Pendientes</option>
                <option value="en preparacion">En preparación</option>
                <option value="completado">Completados</option>
                <option value="cancelado">Cancelados</option>
              </select>
            </div>
          </div>

          {pedidos.length === 0 ? (
            <p className="text-center py-4">No hay pedidos {filtro !== 'todos' ? `con estado "${filtro}"` : 'registrados'}.</p>
          ) : (
            <div className="space-y-4">
              {pedidos.map(pedido => (
                <div key={pedido.id} className={`p-4 border rounded-lg ${getEstadoStyle(pedido.estado)} border-l-4`}>
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold">Pedido #{pedido.id}</h3>
                      <p className="text-sm">Cliente: {pedido.cliente || 'Cliente no especificado'}</p>
                      <p className="text-sm">Fecha: {formatearFecha(pedido.fecha)}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEstadoStyle(pedido.estado)}`}>
                        {pedido.estado}
                      </span>
                    </div>
                  </div>

                  <div className="mt-3">
                    <h4 className="font-medium text-sm mb-1">Productos:</h4>
                    <ul className="space-y-2">
                      {pedido.productos && pedido.productos.map((producto, idx) => (
                        <li key={idx} className="text-sm pl-2">
                          {producto.cantidad}x {producto.nombre} - ${producto.precio}
                          {producto.ingredientes && (
                            <div className="text-xs text-gray-600 pl-3">
                              Ingredientes extra: {producto.ingredientes.join(', ')}
                            </div>
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div className="mt-3 pt-2 border-t flex justify-between items-center">
                    <div className="font-bold">Total: ${pedido.total}</div>
                    <div className="flex space-x-2">
                      <div className="flex space-x-2">
                        {pedido.estado === 'pendiente' && (
                          <>
                            <button 
                              onClick={() => actualizarEstadoPedido(pedido.id, 'en preparacion')}
                              className="px-3 py-1 bg-blue-500 text-white text-xs rounded hover:bg-blue-600"
                            >
                              Preparar
                            </button>
                            <button 
                              onClick={() => actualizarEstadoPedido(pedido.id, 'cancelado')}
                              className="px-3 py-1 bg-red-500 text-white text-xs rounded hover:bg-red-600"
                            >
                              Cancelar
                            </button>
                          </>
                        )}
                        {pedido.estado === 'en preparacion' && (
                          <button 
                            onClick={() => actualizarEstadoPedido(pedido.id, 'completado')}
                            className="px-3 py-1 bg-green-500 text-white text-xs rounded hover:bg-green-600"
                          >
                            Completar
                          </button>
                        )}
                      </div>
                      <button 
                        onClick={() => confirmarEliminacion(pedido)}
                        className="px-3 py-1 bg-gray-500 text-white text-xs rounded hover:bg-gray-600"
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Modal de confirmación para eliminar pedido */}
      {mostrarModalEliminar && pedidoAEliminar && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-lg font-bold mb-4">Confirmar eliminación</h3>
            <p>¿Estás seguro que deseas eliminar el pedido #{pedidoAEliminar.id}?</p>
            <p className="text-sm text-gray-600 mt-2">Esta acción no se puede deshacer.</p>
            
            <div className="flex justify-end space-x-3 mt-6">
              <button 
                onClick={() => setMostrarModalEliminar(false)}
                className="px-4 py-2 border border-gray-300 rounded hover:bg-gray-100"
              >
                Cancelar
              </button>
              <button 
                onClick={eliminarPedido}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Eliminar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminPedidos;
