import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/register.css';

function AdminPedidos() {
  const [pedidos, setPedidos] = useState([]);
  const [detallesPedidos, setDetallesPedidos] = useState({});
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);
  const [filtro, setFiltro] = useState('todos');
  const [mostrarModalConfirmacion, setMostrarModalConfirmacion] = useState(false);
  const [pedidoAEliminar, setPedidoAEliminar] = useState(null);

  const cargarPedidos = async () => {
    try {
      setCargando(true);
      setError(null);
      
      // Cargar pedidos principales
      const responsePedidos = await fetch('http://localhost/back_your_nutrition/public/pedidos');
      
      if (!responsePedidos.ok) {
        throw new Error('Error al cargar pedidos');
      }
      
      const dataPedidos = await responsePedidos.json();
      setPedidos(dataPedidos);

      // Cargar detalles de cada pedido
      const detalles = {};
      for (const pedido of dataPedidos) {
        const responseDetalles = await fetch(`http://localhost/back_your_nutrition/public/detalle-pedido/${pedido.id_pedidos}`);
        if (responseDetalles.ok) {
          detalles[pedido.id_pedidos] = await responseDetalles.json();
        }
      }
      setDetallesPedidos(detalles);
    } catch (err) {
      console.error('Error:', err);
      setError(err.message);
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarPedidos();
  }, []);

  const actualizarEstado = async (pedidoId, nuevoEstado) => {
    try {
      const response = await fetch(
        `http://localhost/back_your_nutrition/public/pedidos/${pedidoId}`, 
        {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ estado: nuevoEstado })
        }
      );

      if (!response.ok) {
        throw new Error('Error al actualizar estado');
      }

      // Actualizar el estado localmente
      setPedidos(pedidos.map(p => 
        p.id_pedidos === pedidoId ? { ...p, estado: nuevoEstado } : p
      ));
    } catch (err) {
      console.error('Error al actualizar estado:', err);
      setError(err.message);
    }
  };

  const confirmarEliminacion = (pedido) => {
    setPedidoAEliminar(pedido);
    setMostrarModalConfirmacion(true);
  };

  const eliminarPedido = async () => {
    if (!pedidoAEliminar) return;
    
    try {
      // Primero eliminar los detalles del pedido
      const responseDetalles = await fetch(
        `http://localhost/back_your_nutrition/public/detalle-pedido/${pedidoAEliminar.id_pedidos}`, 
        {
          method: 'DELETE'
        }
      );

      if (!responseDetalles.ok) {
        throw new Error('Error al eliminar detalles del pedido');
      }

      // Luego eliminar el pedido principal
      const responsePedido = await fetch(
        `http://localhost/back_your_nutrition/public/pedidos/${pedidoAEliminar.id_pedidos}`, 
        {
          method: 'DELETE'
        }
      );

      if (!responsePedido.ok) {
        throw new Error('Error al eliminar pedido');
      }

      // Actualizar la lista localmente
      setPedidos(pedidos.filter(p => p.id_pedidos !== pedidoAEliminar.id_pedidos));
      setMostrarModalConfirmacion(false);
    } catch (err) {
      console.error('Error al eliminar pedido:', err);
      setError(err.message);
      setMostrarModalConfirmacion(false);
    }
  };

  const filtrarPedidos = () => {
    if (filtro === 'todos') return pedidos;
    return pedidos.filter(pedido => pedido.estado === filtro);
  };

  const getEstadoStyle = (estado) => {
    switch (estado) {
      case 'pendiente': return 'bg-yellow-100 text-yellow-800 border-l-yellow-500';
      case 'en preparacion': return 'bg-blue-100 text-blue-800 border-l-blue-500';
      case 'completado': return 'bg-green-100 text-green-800 border-l-green-500';
      case 'cancelado': return 'bg-red-100 text-red-800 border-l-red-500';
      default: return 'bg-gray-100 text-gray-800 border-l-gray-500';
    }
  };

  const pedidosFiltrados = filtrarPedidos();

  if (cargando) return (
    <div className="p-4 text-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
      <p>Cargando pedidos...</p>
    </div>
  );

  if (error) return (
    <div className="p-4 bg-red-100 text-red-700 rounded-lg mx-4">
      <h3 className="font-bold">Error</h3>
      <p>{error}</p>
      <button 
        onClick={cargarPedidos}
        className="mt-2 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
      >
        Reintentar
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h2 className="text-xl font-bold">S´ FOR YOUR NUTRITION</h2>
            </div>
            <div className="flex space-x-4">
              <Link to="/admin/pedidos" className="px-3 py-2 rounded-md text-sm font-medium bg-gray-200">
                Pedidos
              </Link>
              <Link to="/admin/notificaciones" className="px-3 py-2 rounded-md text-sm font-medium">
                Notificaciones
              </Link>
              <Link to="/admin/inventario" className="px-3 py-2 rounded-md text-sm font-medium">
                Inventario
              </Link>
              <Link to="/admin/micuenta" className="px-3 py-2 rounded-md text-sm font-medium">
                Mi Cuenta
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold">Gestión de Pedidos</h2>
            <select
              value={filtro}
              onChange={(e) => setFiltro(e.target.value)}
              className="p-2 border rounded"
            >
              <option value="todos">Todos los pedidos</option>
              <option value="pendiente">Pendientes</option>
              <option value="en preparacion">En preparación</option>
              <option value="completado">Completados</option>
              <option value="cancelado">Cancelados</option>
            </select>
          </div>

          {pedidosFiltrados.length === 0 ? (
            <p>No hay pedidos {filtro !== 'todos' ? `con estado "${filtro}"` : 'registrados'}.</p>
          ) : (
            <div className="space-y-4">
              {pedidosFiltrados.map(pedido => (
                <div key={pedido.id_pedidos} className={`p-4 border rounded-lg ${getEstadoStyle(pedido.estado)} border-l-4`}>
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-bold">Pedido #{pedido.id_pedidos}</h3>
                      <p className="text-sm">Cliente ID: {pedido.cliente_id}</p>
                      <p className="text-sm">Fecha: {new Date(pedido.fecha).toLocaleString()}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEstadoStyle(pedido.estado)}`}>
                        {pedido.estado}
                      </span>
                    </div>
                  </div>

                  <div className="mt-3">
                    <h4 className="font-medium text-sm mb-1">Productos:</h4>
                    <ul className="space-y-2">
                      {detallesPedidos[pedido.id_pedidos]?.map((detalle, idx) => (
                        <li key={idx} className="text-sm pl-2">
                          {detalle.cantidad}x {detalle.producto} - ${detalle.precio_unitario}
                          {detalle.ingredientes_adicionales && (
                            <div className="text-xs text-gray-600 pl-3">
                              Ingredientes extra: {detalle.ingredientes_adicionales}
                            </div>
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div className="mt-3 pt-2 border-t flex justify-between items-center">
                    <div className="font-bold">Total: ${pedido.total}</div>
                    <div className="flex space-x-2">
                      <select
                        value={pedido.estado}
                        onChange={(e) => actualizarEstado(pedido.id_pedidos, e.target.value)}
                        className="p-1 border rounded text-sm"
                      >
                        <option value="pendiente">Pendiente</option>
                        <option value="en preparacion">En preparación</option>
                        <option value="completado">Completado</option>
                        <option value="cancelado">Cancelado</option>
                      </select>
                      <button 
                        onClick={() => confirmarEliminacion(pedido)}
                        className="px-3 py-1 bg-red-500 text-white text-xs rounded hover:bg-red-600"
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Modal de confirmación de eliminación */}
      {mostrarModalConfirmacion && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h3 className="text-lg font-bold mb-4">Confirmar eliminación</h3>
            <p>¿Estás seguro que deseas eliminar el pedido #{pedidoAEliminar?.id_pedidos}?</p>
            <div className="flex justify-end space-x-4 mt-6">
              <button 
                onClick={() => setMostrarModalConfirmacion(false)}
                className="px-4 py-2 border rounded"
              >
                Cancelar
              </button>
              <button 
                onClick={eliminarPedido}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Eliminar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminPedidos;
