// src/components/Menu.js
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';
import { FaBars, FaTimes } from 'react-icons/fa';

function Menu() {
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  // Ocultar en rutas de admin/cliente
  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      {/* Menú hamburguesa */}
      <div className="hamburger-menu" onClick={toggleMenu}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </div>

      {/* Menú lateral */}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="menu-header">
          <h1>S' FOR YOUR NUTRITION</h1>
          <h2>Bienvenido nuevamente</h2>
        </div>

        <nav className="side-nav">
          <Link to="/productos" onClick={() => setIsOpen(false)}>Productos</Link>
          <Link to="/comprar" onClick={() => setIsOpen(false)}>Ordenar</Link>
          <Link to="/carrito" onClick={() => setIsOpen(false)}>Carrito</Link>
          <Link to="/mi-cuenta" onClick={() => setIsOpen(false)}>Mi Cuenta</Link>
        </nav>
      </div>

      {/* Overlay para cerrar el menú al hacer clic fuera */}
      {isOpen && <div className="overlay" onClick={toggleMenu} />}
    </>
  );
}

export default Menu;
