import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

class Menu extends React.Component {
  state = {
    isOpen: false
  };

  toggleMenu = () => {
    this.setState(prevState => ({ isOpen: !prevState.isOpen }));
  };

  render() {
    const userRole = localStorage.getItem('userRole');
    const location = this.props.location || useLocation();

    if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
      return null;
    }

    return (
      <>
        <div className="hamburger-btn" onClick={this.toggleMenu}>
          <div className={`hamburger-line ${this.state.isOpen ? 'open' : ''}`}></div>
          <div className={`hamburger-line ${this.state.isOpen ? 'open' : ''}`}></div>
          <div className={`hamburger-line ${this.state.isOpen ? 'open' : ''}`}></div>
        </div>

        <div className={`sidebar ${this.state.isOpen ? 'open' : ''}`}>
          <div className="menu-header">
            <h1>S' FOR YOUR NUTRITION</h1>
            <h2>Bienvenido nuevamente</h2>
          </div>

          <nav className="side-nav">
            <Link to="/productos" onClick={this.toggleMenu}>Productos</Link>
            <Link to="/comprar" onClick={this.toggleMenu}>Ordenar</Link>
            <Link to="/carrito" onClick={this.toggleMenu}>Carrito</Link>
            <Link to="/mi-cuenta" onClick={this.toggleMenu}>Mi Cuenta</Link>
          </nav>
        </div>

        {this.state.isOpen && <div className="overlay" onClick={this.toggleMenu} />}
      </>
    );
  }
}

export default Menu;
