import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    Nombre: '',
    Precio: '',
    Cantidad: '0',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Disponibilidad: '1'
  });
  const [editandoId, setEditandoId] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  // Cargar productos al montar el componente
  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const respuesta = await fetch('http://localhost:8000/productos');
      
      if (!respuesta.ok) {
        throw new Error(`Error HTTP: ${respuesta.status}`);
      }
      
      const datos = await respuesta.json();
      setProductos(datos);
    } catch (err) {
      console.error('Error al cargar productos:', err);
      setError(`Error al cargar productos: ${err.message}`);
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validarFormulario = () => {
    const errores = [];
    
    if (!formData.Nombre.trim()) errores.push('El nombre es requerido');
    if (!formData.Precio || isNaN(formData.Precio)) errores.push('Precio debe ser un número');
    if (!formData.Tamaño) errores.push('Seleccione un tamaño');
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    
    return true;
  };

  const iniciarEdicion = (producto) => {
    if (!producto || !producto.id) {
      setError('Producto no válido para edición');
      return;
    }
    
    setEditandoId(producto.id);
    setFormData({
      Nombre: producto.Nombre || '',
      Precio: producto.Precio?.toString() || '',
      Cantidad: producto.Cantidad?.toString() || '0',
      Descripcion: producto.Descripcion || '',
      Tamaño: producto.Tamaño || '',
      Calorias: producto.Calorias?.toString() || '',
      Disponibilidad: producto.Disponibilidad?.toString() || '1'
    });
  };

  const cancelarEdicion = () => {
    setEditandoId(null);
    setFormData({
      Nombre: '',
      Precio: '',
      Cantidad: '0',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Disponibilidad: '1'
    });
    setError('');
  };

  const guardarProducto = async () => {
    if (!validarFormulario()) return;
    
    try {
      const productoData = {
        Nombre: formData.Nombre,
        Precio: parseFloat(formData.Precio),
        Cantidad: parseInt(formData.Cantidad),
        Descripcion: formData.Descripcion,
        Tamaño: formData.Tamaño,
        Calorias: formData.Calorias ? parseInt(formData.Calorias) : null,
        Disponibilidad: parseInt(formData.Disponibilidad)
      };

      let response;
      let url;
      
      if (editandoId) {
        // Verificar que el ID existe
        if (!editandoId) {
          throw new Error('ID de producto no válido');
        }
        
        url = `http://localhost:8000/productos/${editandoId}`;
        response = await fetch(url, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productoData)
        });
      } else {
        url = 'http://localhost:8000/productos';
        response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productoData)
        });
      }

      // Verificar respuesta
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || `Error ${response.status}`);
      }

      const productoGuardado = await response.json();
      
      // Actualizar estado local
      if (editandoId) {
        setProductos(productos.map(p => 
          p.id === productoGuardado.id ? productoGuardado : p
        ));
      } else {
        setProductos([...productos, productoGuardado]);
      }
      
      cancelarEdicion();
      alert(`Producto ${editandoId ? 'actualizado' : 'creado'} correctamente`);
      
    } catch (error) {
      console.error('Error al guardar:', error);
      setError(error.message.includes('404') 
        ? 'Producto no encontrado en el servidor' 
        : error.message);
      
      if (error.message.includes('404')) {
        cargarProductos(); // Recargar productos para sincronizar
      }
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este producto?')) return;
    
    try {
      if (!id) {
        throw new Error('ID de producto no válido');
      }
      
      const response = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Error al eliminar producto');
      }

      setProductos(productos.filter(p => p.id !== id));
      
      if (editandoId === id) {
        cancelarEdicion();
      }
      
      alert('Producto eliminado correctamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    }
  };

  if (cargando) {
    return <div className="cargando">Cargando productos...</div>;
  }

  return (
    <div className="inventario-container">
      {/* ... (tu JSX existente) ... */}
    </div>
  );
};

export default Inventario;
