import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    Nombre: '',
    Precio: '',
    Cantidad: '0',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Disponibilidad: '1'
  });
  const [editandoId, setEditandoId] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  // Cargar productos
  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const res = await fetch('http://localhost:8000/productos');
      const data = await res.json();
      setProductos(data);
    } catch (err) {
      setError('Error al cargar productos');
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const iniciarEdicion = (producto) => {
    setEditandoId(producto.id);
    setFormData({
      Nombre: producto.Nombre,
      Precio: producto.Precio,
      Cantidad: producto.Cantidad,
      Descripcion: producto.Descripcion,
      Tamaño: producto.Tamaño,
      Calorias: producto.Calorias,
      Disponibilidad: producto.Disponibilidad
    });
  };

  const cancelarEdicion = () => {
    setEditandoId(null);
    setFormData({
      Nombre: '',
      Precio: '',
      Cantidad: '0',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Disponibilidad: '1'
    });
  };

  const agregarProducto = async () => {
    try {
      const response = await fetch('http://localhost:8000/productos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setProductos([...productos, data]);
      cancelarEdicion();
    } catch (error) {
      setError('Error al agregar producto');
    }
  };

  const guardarModificacion = async () => {
    try {
      const response = await fetch(`http://localhost:8000/productos/${editandoId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setProductos(productos.map(p => p.id === editandoId ? data : p));
      cancelarEdicion();
    } catch (error) {
      setError('Error al guardar cambios');
    }
  };

  const eliminarProducto = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este producto?')) {
      await fetch(`http://localhost:8000/productos/${id}`, { method: 'DELETE' });
      setProductos(productos.filter(p => p.id !== id));
    }
  };

  if (cargando) return <div>Cargando...</div>;

  return (
    <div className="inventario">
      {/* Tu navegación y header aquí */}

      {/* Formulario */}
      <div className="formulario">
        <h2>{editandoId ? 'Editando Producto' : 'Agregar Nuevo Producto'}</h2>
        
        <input name="Nombre" value={formData.Nombre} onChange={handleChange} placeholder="Nombre" />
        <input name="Precio" type="number" value={formData.Precio} onChange={handleChange} placeholder="Precio" />
        <input name="Cantidad" type="number" value={formData.Cantidad} onChange={handleChange} placeholder="Cantidad" />
        <input name="Descripcion" value={formData.Descripcion} onChange={handleChange} placeholder="Descripción" />
        <select name="Tamaño" value={formData.Tamaño} onChange={handleChange}>
          <option value="">Seleccione tamaño</option>
          <option value="100">100 ml</option>
          <option value="150">150 ml</option>
          <option value="250">250 ml</option>
        </select>
        <input name="Calorias" type="number" value={formData.Calorias} onChange={handleChange} placeholder="Calorías" />
        <select name="Disponibilidad" value={formData.Disponibilidad} onChange={handleChange}>
          <option value="1">Disponible</option>
          <option value="0">No disponible</option>
        </select>

        <div className="botones-accion">
          {editandoId ? (
            <>
              <button className="btn-guardar" onClick={guardarModificacion}>
                Guardar Cambios
              </button>
              <button className="btn-cancelar" onClick={cancelarEdicion}>
                Cancelar
              </button>
            </>
          ) : (
            <button className="btn-agregar" onClick={agregarProducto}>
              Agregar Producto
            </button>
          )}
        </div>
      </div>

      {/* Tabla de productos */}
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Descripción</th>
            <th>Tamaño</th>
            <th>Calorías</th>
            <th>Cantidad</th>
            <th>Disponible</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(producto => (
            <tr key={producto.id}>
              <td>{producto.Nombre}</td>
              <td>${producto.Precio}</td>
              <td>{producto.Descripcion}</td>
              <td>{producto.Tamaño} ml</td>
              <td>{producto.Calorias}</td>
              <td>{producto.Cantidad}</td>
              <td>{producto.Disponibilidad ? 'Sí' : 'No'}</td>
              <td>
                <button className="btn-editar" onClick={() => iniciarEdicion(producto)}>
                  Editar
                </button>
                <button className="btn-eliminar" onClick={() => eliminarProducto(producto.id)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Inventario;
