import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    Nombre: '',
    Precio: '',
    Cantidad: '0',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Disponibilidad: '1'
  });
  const [editandoId, setEditandoId] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error(`Error: ${res.status}`);
      const data = await res.json();
      setProductos(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Error cargando productos:", err);
      setError("Error al cargar productos");
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validarFormulario = () => {
    const errores = [];
    if (!formData.Nombre.trim()) errores.push('Nombre es requerido');
    if (!formData.Precio || isNaN(formData.Precio)) errores.push('Precio debe ser número');
    if (!formData.Tamaño) errores.push('Seleccione tamaño');
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    return true;
  };

  const iniciarEdicion = (producto) => {
    if (!producto || typeof producto !== 'object') {
      setError('Objeto producto no válido');
      return;
    }
    
    if (!producto.id && !producto.Nombre) {
      setError('Producto no tiene ID ni Nombre válido');
      return;
    }
    
    setEditandoId(producto.id || null);
    setFormData({
      Nombre: producto.Nombre || '',
      Precio: producto.Precio?.toString() || '',
      Cantidad: producto.Cantidad?.toString() || '0',
      Descripcion: producto.Descripcion || '',
      Tamaño: producto.Tamaño || '',
      Calorias: producto.Calorias?.toString() || '',
      Disponibilidad: producto.Disponibilidad?.toString() || '1'
    });
  };

  const cancelarEdicion = () => {
    setEditandoId(null);
    setFormData({
      Nombre: '',
      Precio: '',
      Cantidad: '0',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Disponibilidad: '1'
    });
    setError('');
  };

  const guardarProducto = async () => {
    if (!validarFormulario()) return;

    try {
      const productoData = {
        Nombre: formData.Nombre,
        Precio: parseFloat(formData.Precio),
        Cantidad: parseInt(formData.Cantidad),
        Descripcion: formData.Descripcion,
        Tamaño: formData.Tamaño,
        Calorias: formData.Calorias ? parseInt(formData.Calorias) : null,
        Disponibilidad: parseInt(formData.Disponibilidad)
      };

      let url = 'http://localhost:8000/productos';
      let method = 'POST';
      
      if (editandoId) {
        const productoExistente = productos.find(p => p.id === editandoId);
        if (!productoExistente) {
          throw new Error('El producto que intentas editar no existe en la lista local');
        }
        
        url += `/${editandoId}`;
        method = 'PUT';
      }

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(productoData)
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Error ${response.status}`);
      }

      const productoGuardado = await response.json();
      
      // Actualizar estado local
      if (editandoId) {
        setProductos(productos.map(p => p.id === editandoId ? productoGuardado : p));
      } else {
        setProductos([...productos, productoGuardado]);
      }
      
      cancelarEdicion();
      alert(`Producto ${editandoId ? 'actualizado' : 'creado'} correctamente`);
    } catch (error) {
      console.error("Error guardando producto:", error);
      setError(error.message);
      
      if (error.message.includes('404') || error.message.includes('no existe')) {
        cargarProductos(); // Recargar para sincronizar
      }
    }
  };

  const eliminarProducto = async (id) => {
    if (!id) {
      setError('ID no válido para eliminar');
      return;
    }
    
    if (!window.confirm('¿Estás seguro de eliminar este producto?')) return;
    
    try {
      const response = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error(`Error ${response.status}`);

      setProductos(productos.filter(p => p.id !== id));
      
      if (editandoId === id) {
        cancelarEdicion();
      }
      
      alert('Producto eliminado correctamente');
    } catch (error) {
      console.error("Error eliminando producto:", error);
      setError(error.message);
    }
  };

  if (cargando) return <div className="cargando">Cargando productos...</div>;

  return (
    <div className="inventario-container">
      {/* Header y navegación */}
      <h1>Inventario</h1>
      
      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>×</button>
        </div>
      )}

      {/* Formulario */}
      <div className="formulario-producto">
        <div className="form-group">
          <label>Nombre</label>
          <input
            name="Nombre"
            value={formData.Nombre}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Precio ($)</label>
            <input
              name="Precio"
              type="number"
              min="0"
              step="0.01"
              value={formData.Precio}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Cantidad</label>
            <input
              name="Cantidad"
              type="number"
              min="0"
              value={formData.Cantidad}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Descripción</label>
          <textarea
            name="Descripcion"
            value={formData.Descripcion}
            onChange={handleChange}
            rows="3"
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Tamaño (ml)</label>
            <select
              name="Tamaño"
              value={formData.Tamaño}
              onChange={handleChange}
              required
            >
              <option value="">Seleccionar...</option>
              <option value="100">100 ml</option>
              <option value="150">150 ml</option>
              <option value="250">250 ml</option>
            </select>
          </div>

          <div className="form-group">
            <label>Calorías</label>
            <input
              name="Calorias"
              type="number"
              min="0"
              value={formData.Calorias}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Disponibilidad</label>
            <select
              name="Disponibilidad"
              value={formData.Disponibilidad}
              onChange={handleChange}
            >
              <option value="1">Disponible</option>
              <option value="0">No disponible</option>
            </select>
          </div>
        </div>

        <div className="form-actions">
          {editandoId ? (
            <>
              <button onClick={guardarProducto}>Guardar Cambios</button>
              <button onClick={cancelarEdicion}>Cancelar</button>
            </>
          ) : (
            <button onClick={guardarProducto}>Agregar Producto</button>
          )}
        </div>
      </div>

      {/* Tabla de productos */}
      <div className="tabla-productos">
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Descripción</th>
              <th>Tamaño</th>
              <th>Calorías</th>
              <th>Cantidad</th>
              <th>Disponible</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {productos.length === 0 ? (
              <tr>
                <td colSpan="8">No hay productos registrados</td>
              </tr>
            ) : (
              productos.map(producto => (
                <tr key={`producto-${producto.id}`}>
                  <td>{producto.Nombre}</td>
                  <td>${producto.Precio?.toFixed(2)}</td>
                  <td>{producto.Descripcion}</td>
                  <td>{producto.Tamaño} ml</td>
                  <td>{producto.Calorias || '-'}</td>
                  <td>{producto.Cantidad}</td>
                  <td>{producto.Disponibilidad ? 'Sí' : 'No'}</td>
                  <td>
                    <button onClick={() => iniciarEdicion(producto)}>Editar</button>
                    <button onClick={() => eliminarProducto(producto.id)}>Eliminar</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventario;
