import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  // Estado para los productos
  const [productos, setProductos] = useState([]);
  
  // Estado para el formulario
  const [formData, setFormData] = useState({
    Nombre: '',
    Precio: '',
    Cantidad: '0',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Disponibilidad: '1'
  });

  const [editandoId, setEditandoId] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  // Cargar productos al montar el componente
  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const respuesta = await fetch('http://localhost:8000/productos');
      
      if (!respuesta.ok) {
        throw new Error(`Error HTTP: ${respuesta.status}`);
      }
      
      const datos = await respuesta.json();
      setProductos(datos);
    } catch (err) {
      console.error('Error al cargar productos:', err);
      setError(`Error al cargar productos: ${err.message}`);
    } finally {
      setCargando(false);
    }
  };

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Validar el formulario
  const validarFormulario = () => {
    const errores = [];
    
    if (!formData.Nombre.trim()) errores.push('El nombre es requerido');
    if (!formData.Precio || isNaN(formData.Precio)) errores.push('Precio debe ser un número');
    if (!formData.Tamaño) errores.push('Seleccione un tamaño');
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    
    return true;
  };

  // Iniciar edición de producto
  const iniciarEdicion = (producto) => {
    if (!producto || !producto.Nombre) {
      setError('Producto no válido para edición');
      return;
    }
    
    setEditandoId(producto.Nombre); // Usamos el nombre como ID temporal
    setFormData({
      Nombre: producto.Nombre || '',
      Precio: producto.Precio?.toString() || '',
      Cantidad: producto.Cantidad?.toString() || '0',
      Descripcion: producto.Descripcion || '',
      Tamaño: producto.Tamaño || '',
      Calorias: producto.Calorias?.toString() || '',
      Disponibilidad: producto.Disponibilidad?.toString() || '1'
    });
  };

  // Cancelar edición
  const cancelarEdicion = () => {
    setEditandoId(null);
    setFormData({
      Nombre: '',
      Precio: '',
      Cantidad: '0',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Disponibilidad: '1'
    });
    setError('');
  };

  

  // Eliminar producto
  const eliminarProducto = async (nombreProducto) => {
    if (!window.confirm(`¿Eliminar el producto "${nombreProducto}"?`)) return;
    
    try {
      const response = await fetch(`http://localhost:8000/productos/${encodeURIComponent(nombreProducto)}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Error al eliminar producto');
      }

      setProductos(productos.filter(p => p.Nombre !== nombreProducto));
      
      if (editandoId === nombreProducto) {
        cancelarEdicion();
      }
      
      alert('Producto eliminado correctamente');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    }
  };

  if (cargando) {
    return <div className="cargando">Cargando productos...</div>;
  }

  return (
    <div className="inventario-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo"><h2>S´ FOR YOUR NUTRITION</h2></div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <h1>Bienvenido nuevamente Admin🥤</h1>

      <div className="inventario">
        <h2>Inventario</h2>

        {error && <div className="error-message">{error}</div>}

        {/* Formulario de producto */}
        <div className="formulario-producto">
          <div className="form-group">
            <label>Nombre</label>
            <input
              name="Nombre"
              value={formData.Nombre}
              onChange={handleChange}
              placeholder="Nombre del producto"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Precio ($)</label>
              <input
                name="Precio"
                type="number"
                min="0"
                step="0.01"
                value={formData.Precio}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Cantidad</label>
              <input
                name="Cantidad"
                type="number"
                min="0"
                value={formData.Cantidad}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="form-group">
            <label>Descripción</label>
            <textarea
              name="Descripcion"
              value={formData.Descripcion}
              onChange={handleChange}
              rows="3"
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Tamaño (ml)</label>
              <select
                name="Tamaño"
                value={formData.Tamaño}
                onChange={handleChange}
                required
              >
                <option value="">Seleccionar...</option>
                <option value="100">100 ml</option>
                <option value="150">150 ml</option>
                <option value="250">250 ml</option>
              </select>
            </div>

            <div className="form-group">
              <label>Calorías</label>
              <input
                name="Calorias"
                type="number"
                min="0"
                value={formData.Calorias}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Disponibilidad</label>
              <select
                name="Disponibilidad"
                value={formData.Disponibilidad}
                onChange={handleChange}
              >
                <option value="1">Disponible</option>
                <option value="0">No disponible</option>
              </select>
            </div>
          </div>

          <div className="form-actions">
            {editandoId ? (
              <>
                <button onClick={guardarProducto} className="btn-primary">
                  Guardar Cambios
                </button>
                <button onClick={cancelarEdicion} className="btn-secondary">
                  Cancelar
                </button>
              </>
            ) : (
              <button onClick={guardarProducto} className="btn-primary">
                Agregar Producto
              </button>
            )}
          </div>
        </div>

        {/* Tabla de productos */}
        <div className="tabla-productos">
          {productos.length === 0 ? (
            <p className="no-products">No hay productos registrados</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Precio</th>
                  <th>Descripción</th>
                  <th>Tamaño</th>
                  <th>Calorías</th>
                  <th>Cantidad</th>
                  <th>Disponible</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {productos.map((producto) => (
                  <tr key={`producto-${producto.Nombre}`}>
                    <td>{producto.Nombre}</td>
                    <td>${producto.Precio?.toFixed(2)}</td>
                    <td className="descripcion">{producto.Descripcion}</td>
                    <td>{producto.Tamaño} ml</td>
                    <td>{producto.Calorias || '-'}</td>
                    <td>{producto.Cantidad}</td>
                    <td>
                      <span className={`badge ${producto.Disponibilidad ? 'disponible' : 'no-disponible'}`}>
                        {producto.Disponibilidad ? 'Sí' : 'No'}
                      </span>
                    </td>
                    <td className="acciones">
                      <button 
                        onClick={() => iniciarEdicion(producto)}
                        className="btn-action edit"
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => eliminarProducto(producto.Nombre)}
                        className="btn-action delete"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default Inventario;
