import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCarrito } from '../context/CarritoContext';
import '../style/pedidos.css';

function Pedidos() {
  const { carrito, vaciarCarrito, calcularTotal } = useCarrito();
  const [pedidos, setPedidos] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    cliente: '',
    productos: [],
    fecha: '',
    total: ''
  });

  // Cargar pedidos desde localStorage al inicio
  useEffect(() => {
    const savedPedidos = JSON.parse(localStorage.getItem('pedidos')) || [];
    setPedidos(savedPedidos);
  }, []);

  // Guardar pedidos en localStorage cuando cambian
  useEffect(() => {
    localStorage.setItem('pedidos', JSON.stringify(pedidos));
  }, [pedidos]);

  // Registrar pedido desde el carrito
  const registrarPedidoDesdeCarrito = () => {
    if (carrito.length === 0) {
      alert('El carrito está vacío');
      return;
    }

    const nuevoPedido = {
      id: Date.now(),
      cliente: 'Cliente Actual', // Puedes modificar esto para pedir el nombre
      productos: [...carrito], // Copia directa del carrito
      fecha: new Date().toISOString().split('T')[0],
      total: calcularTotal(),
      estado: 'pendiente' // Nuevo campo para estado del pedido
    };

    setPedidos([...pedidos, nuevoPedido]);
    vaciarCarrito();
    alert('Pedido registrado exitosamente');
  };

  const iniciarEdicion = (pedido) => {
    setEditingId(pedido.id);
    setEditForm({
      cliente: pedido.cliente || '',
      productos: pedido.productos || [],
      fecha: pedido.fecha || '',
      total: pedido.total || 0
    });
  };

  const guardarEdicion = () => {
    setPedidos(pedidos.map(pedido => 
      pedido.id === editingId 
        ? { 
            ...pedido,
            ...editForm,
            total: parseFloat(editForm.total) || 0
          } 
        : pedido
    ));
    setEditingId(null);
  };

  const eliminarPedido = (id) => {
    if (window.confirm('¿Estás seguro de eliminar este pedido?')) {
      setPedidos(pedidos.filter(pedido => pedido.id !== id));
    }
  };

  // Función para formatear los productos e ingredientes
  const formatProductos = (productos) => {
    if (!productos || !Array.isArray(productos)) return "No hay productos";
    
    return productos.map(producto => (
      <div key={`${producto.id}-${producto.Nombre}`} className="producto-item">
        <strong>{producto.Nombre}</strong> (x{producto.cantidad}) - ${producto.precio}
        {producto.ingredientesAdicionales?.length > 0 && (
          <ul className="ingredientes-list">
            {producto.ingredientesAdicionales.map((ing, i) => (
              <li key={i}>+ {ing}</li>
            ))}
          </ul>
        )}
      </div>
    ));
  };

  return (
    <div className="pedidos-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S´ FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-pedidos">
        <h2>Gestión de Pedidos</h2>
        
        <div className="acciones-principales">
          <button 
            onClick={registrarPedidoDesdeCarrito}
            className="btn-registrar"
            disabled={carrito.length === 0}
          >
            Registrar Pedido desde Carrito
          </button>
        </div>

        <div className="lista-pedidos">
          {pedidos.length === 0 ? (
            <p>No hay pedidos registrados</p>
          ) : (
            pedidos.map(pedido => (
              <div key={pedido.id} className="pedido-card">
                {editingId === pedido.id ? (
                  <div className="editar-pedido">
                    <label>
                      Cliente:
                      <input
                        type="text"
                        value={editForm.cliente}
                        onChange={(e) => setEditForm({...editForm, cliente: e.target.value})}
                      />
                    </label>
                    
                    <label>
                      Fecha:
                      <input
                        type="date"
                        value={editForm.fecha}
                        onChange={(e) => setEditForm({...editForm, fecha: e.target.value})}
                      />
                    </label>
                    
                    <label>
                      Total:
                      <input
                        type="number"
                        value={editForm.total}
                        onChange={(e) => setEditForm({...editForm, total: e.target.value})}
                      />
                    </label>
                    
                    <div className="productos-edit">
                      <h4>Productos:</h4>
                      {formatProductos(editForm.productos)}
                    </div>
                    
                    <div className="acciones-edicion">
                      <button onClick={guardarEdicion}>Guardar</button>
                      <button onClick={() => setEditingId(null)}>Cancelar</button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="pedido-header">
                      <h3>Pedido #{pedido.id}</h3>
                      <span className="fecha-pedido">{pedido.fecha}</span>
                      <span className={`estado-pedido ${pedido.estado}`}>
                        {pedido.estado || 'pendiente'}
                      </span>
                    </div>
                    <div className="pedido-info">
                      <p><strong>Cliente:</strong> {pedido.cliente || 'No especificado'}</p>
                      <p><strong>Productos:</strong></p>
                      <div className="productos-list">
                        {formatProductos(pedido.productos)}
                      </div>
                      <p><strong>Total:</strong> ${pedido.total?.toFixed(2) || '0.00'}</p>
                    </div>
                    <div className="pedido-acciones">
                      <button 
                        onClick={() => iniciarEdicion(pedido)}
                        className="btn-editar"
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => eliminarPedido(pedido.id)}
                        className="btn-eliminar"
                      >
                        Eliminar
                      </button>
                    </div>
                  </>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Pedidos;
