// src/components/Menu.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  // Ocultar en rutas de admin/cliente
  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  return (
    <div className="menu-container">
      <div className="welcome-section">
        <h1>S' FOR YOUR NUTRITION</h1>
        <h2>Bienvenido nuevamente</h2>
      </div>

      <nav className="main-nav">
        <div className="nav-column">
          <div className="nav-item">
            <span>Tr√°bulos</span>
            <div className="submenu">
              <Link to="/sub1">Sub Elemento #1</Link>
            </div>
          </div>
        </div>

        <div className="nav-column">
          <div className="nav-item">
            <span>Proyectos</span>
            <div className="submenu">
              <Link to="/sub2">Sub Elemento #2</Link>
            </div>
          </div>
        </div>

        <div className="nav-column">
          <div className="nav-item">
            <span>Responsive</span>
            <div className="submenu">
              <Link to="/sub3">Sub Elemento #3</Link>
            </div>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Menu;
