// src/components/Menu.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  // Ocultar en rutas de admin/cliente y en la página principal (login)
  if (userRole || location.pathname.startsWith('/admin') || 
      location.pathname.startsWith('/cliente') || location.pathname === '/') {
    return null;
  }

  return (
    <div className="menu-container">
      <div className="welcome-section">

      </div>

      <nav className="main-nav">
        {/* ... (mantén el resto de tu código del menú igual) ... */}
      </nav>
    </div>
  );
}

export default Menu;
