import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCarrito } from '../context/CarritoContext';
import '../style/pedidos.css';

function Pedidos() {
  const { carrito, vaciarCarrito, calcularTotal } = useCarrito();
  const [pedidos, setPedidos] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    cliente: '',
    productos: [],
    fecha: '',
    total: 0,
    estado: 'pendiente'
  });
  const [todosIngredientes, setTodosIngredientes] = useState([]);
  const [editingProductIndex, setEditingProductIndex] = useState(null);

  // Cargar ingredientes disponibles
  useEffect(() => {
    fetch('http://localhost/back_your_nutrition/public/ingredientes')
      .then(response => response.json())
      .then(data => setTodosIngredientes(data))
      .catch(error => console.error('Error cargando ingredientes:', error));
  }, []);

  // Cargar pedidos desde localStorage al inicio
  useEffect(() => {
    const savedPedidos = JSON.parse(localStorage.getItem('pedidos')) || [];
    setPedidos(savedPedidos);
  }, []);

  // Guardar pedidos en localStorage cuando cambian
  useEffect(() => {
    localStorage.setItem('pedidos', JSON.stringify(pedidos));
  }, [pedidos]);

  // Sincronizar con el carrito cuando se finaliza compra
  useEffect(() => {
    const pedidoFinalizado = JSON.parse(localStorage.getItem('ultimoPedido'));
    if (pedidoFinalizado) {
      setPedidos(prev => [...prev, pedidoFinalizado]);
      localStorage.removeItem('ultimoPedido');
    }
  }, []);

  // Calcular total automáticamente cuando cambian los productos
  useEffect(() => {
    if (editingId !== null) {
      const nuevoTotal = editForm.productos.reduce(
        (sum, producto) => sum + (producto.precio * producto.cantidad), 
        0
      );
      setEditForm(prev => ({ ...prev, total: nuevoTotal }));
    }
  }, [editForm.productos, editingId]);

  const iniciarEdicion = (pedido) => {
    setEditingId(pedido.id);
    setEditForm({
      cliente: pedido.cliente || '',
      productos: [...pedido.productos],
      fecha: pedido.fecha || '',
      total: pedido.total || 0,
      estado: pedido.estado || 'pendiente'
    });
    setEditingProductIndex(null);
  };

  const guardarEdicion = () => {
    setPedidos(pedidos.map(pedido => 
      pedido.id === editingId 
        ? { 
            ...pedido,
            cliente: editForm.cliente,
            productos: editForm.productos,
            fecha: editForm.fecha,
            total: editForm.total,
            estado: editForm.estado
          } 
        : pedido
    ));
    setEditingId(null);
  };

  const eliminarPedido = (id) => {
    if (window.confirm('¿Estás seguro de eliminar este pedido?')) {
      setPedidos(pedidos.filter(pedido => pedido.id !== id));
    }
  };

  const actualizarCantidadProducto = (index, cantidad) => {
    if (cantidad > 0) {
      const nuevosProductos = [...editForm.productos];
      nuevosProductos[index] = {
        ...nuevosProductos[index],
        cantidad: cantidad
      };
      setEditForm({ ...editForm, productos: nuevosProductos });
    }
  };

  const toggleIngrediente = (productIndex, ingrediente) => {
    const nuevosProductos = [...editForm.productos];
    const producto = nuevosProductos[productIndex];
    
    const nuevosIngredientes = producto.ingredientesAdicionales?.includes(ingrediente)
      ? producto.ingredientesAdicionales.filter(ing => ing !== ingrediente)
      : [...(producto.ingredientesAdicionales || []), ingrediente];
    
    nuevosProductos[productIndex] = {
      ...producto,
      ingredientesAdicionales: nuevosIngredientes
    };
    
    setEditForm({ ...editForm, productos: nuevosProductos });
  };

  const eliminarProducto = (index) => {
    if (window.confirm('¿Eliminar este producto del pedido?')) {
      const nuevosProductos = [...editForm.productos];
      nuevosProductos.splice(index, 1);
      setEditForm({ ...editForm, productos: nuevosProductos });
      setEditingProductIndex(null);
    }
  };

  const estadosPosibles = [
    'pendiente',
    'confirmado',
    'en preparación',
    'listo para entrega',
    'entregado',
    'cancelado'
  ];

  const formatProductos = (productos, isEditable = false) => {
    if (!productos || !Array.isArray(productos)) return "No hay productos";
    
    return productos.map((producto, index) => (
      <div key={`${producto.id}-${producto.Nombre}-${index}`} className="producto-item">
        {isEditable && editingProductIndex === index ? (
          <div className="editar-producto">
            <h4>Editando: {producto.Nombre}</h4>
            
            <div className="form-group">
              <label>Cantidad:</label>
              <div className="cantidad-control">
                <button 
                  onClick={() => actualizarCantidadProducto(index, producto.cantidad - 1)}
                  disabled={producto.cantidad <= 1}
                >
                  -
                </button>
                <span>{producto.cantidad}</span>
                <button onClick={() => actualizarCantidadProducto(index, producto.cantidad + 1)}>
                  +
                </button>
              </div>
            </div>
            
            <div className="form-group">
              <label>Ingredientes adicionales:</label>
              <div className="ingredientes-options">
                {todosIngredientes.map(ing => (
                  <label key={ing.Id} className="ingrediente-option">
                    <input
                      type="checkbox"
                      checked={producto.ingredientesAdicionales?.includes(ing.Nombre)}
                      onChange={() => toggleIngrediente(index, ing.Nombre)}
                    />
                    {ing.Nombre}
                  </label>
                ))}
              </div>
            </div>
            
            <div className="acciones-producto">
              <button 
                onClick={() => setEditingProductIndex(null)}
                className="btn-guardar"
              >
                Guardar
              </button>
              <button 
                onClick={() => eliminarProducto(index)}
                className="btn-eliminar"
              >
                Eliminar Producto
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="producto-header">
              <h4>{producto.Nombre}</h4>
              {isEditable && (
                <button 
                  onClick={() => setEditingProductIndex(index)}
                  className="btn-editar-producto"
                >
                  Editar
                </button>
              )}
            </div>
            <p>
              <strong>Cantidad:</strong> {producto.cantidad} | 
              <strong> Precio unitario:</strong> ${producto.precio} | 
              <strong> Subtotal:</strong> ${(producto.precio * producto.cantidad).toFixed(2)}
            </p>
            {producto.ingredientesAdicionales?.length > 0 && (
              <ul className="ingredientes-list">
                {producto.ingredientesAdicionales.map((ing, i) => (
                  <li key={i}>+ {ing}</li>
                ))}
              </ul>
            )}
          </>
        )}
      </div>
    ));
  };

  return (
    <div className="pedidos-container">
      <nav className="menu-cliente">
        <div className="contenido-cliente">
          <div className="logo">
            <h2>S´ FOR YOUR NUTRITION</h2>
          </div>
          <ul className="menu-links">
            <li><Link to="/pedidos">Pedidos</Link></li>
            <li><Link to="/notificaciones">Notificaciones</Link></li>
            <li><Link to="/inventario">Inventario</Link></li>
            <li><Link to="/micuentaadmin">Mi Cuenta</Link></li>
          </ul>
        </div>
      </nav>

      <div className="contenido-pedidos">
        <h2>Gestión de Pedidos</h2>
        
        <div className="lista-pedidos">
          {pedidos.length === 0 ? (
            <p className="no-pedidos">No hay pedidos registrados</p>
          ) : (
            pedidos.map(pedido => (
              <div key={pedido.id} className="pedido-card">
                {editingId === pedido.id ? (
                  <div className="editar-pedido">
                    <div className="form-group">
                      <label>Cliente:</label>
                      <input
                        type="text"
                        value={editForm.cliente}
                        onChange={(e) => setEditForm({...editForm, cliente: e.target.value})}
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Fecha:</label>
                      <input
                        type="date"
                        value={editForm.fecha}
                        onChange={(e) => setEditForm({...editForm, fecha: e.target.value})}
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Estado:</label>
                      <select
                        value={editForm.estado}
                        onChange={(e) => setEditForm({...editForm, estado: e.target.value})}
                      >
                        {estadosPosibles.map(estado => (
                          <option key={estado} value={estado}>
                            {estado}
                          </option>
                        ))}
                      </select>
                    </div>
                    
                    <div className="form-group">
                      <label>Total:</label>
                      <div className="total-display">
                        ${editForm.total.toFixed(2)}
                      </div>
                    </div>
                    
                    <div className="productos-edit">
                      <h4>Productos:</h4>
                      {formatProductos(editForm.productos, true)}
                    </div>
                    
                    <div className="acciones-edicion">
                      <button onClick={guardarEdicion} className="btn-guardar">
                        Guardar Cambios
                      </button>
                      <button 
                        onClick={() => setEditingId(null)}
                        className="btn-cancelar"
                      >
                        Cancelar
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="pedido-header">
                      <h3>Pedido #{pedido.id}</h3>
                      <div className="pedido-meta">
                        <span className="fecha-pedido">{pedido.fecha}</span>
                        <span className={`estado-pedido ${pedido.estado}`}>
                          {pedido.estado || 'pendiente'}
                        </span>
                      </div>
                    </div>
                    <div className="pedido-info">
                      <p><strong>Cliente:</strong> {pedido.cliente || 'No especificado'}</p>
                      <p><strong>Productos:</strong></p>
                      <div className="productos-list">
                        {formatProductos(pedido.productos)}
                      </div>
                      <p className="total-pedido">
                        <strong>Total:</strong> ${pedido.total?.toFixed(2) || '0.00'}
                      </p>
                    </div>
                    <div className="pedido-acciones">
                      <button 
                        onClick={() => iniciarEdicion(pedido)}
                        className="btn-editar"
                      >
                        Editar Pedido
                      </button>
                      <button 
                        onClick={() => eliminarPedido(pedido.id)}
                        className="btn-eliminar"
                      >
                        Eliminar Pedido
                      </button>
                    </div>
                  </>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Pedidos;
