function Bienvenida() {
  const [persona, setPersona] = useState(null);

  useEffect(() => {
    // Recuperar los datos de la persona del localStorage
    const storedPersona = JSON.parse(localStorage.getItem('persona'));
    if (storedPersona) {
      setPersona(storedPersona); // Si existe, actualizar el estado con los datos de la persona
    }
  }, []);

  return (
    <div className="bienvenida-container">
      <h1>Bienvenido {persona?.Nombres || 'Cliente'}</h1>
      {/* Aquí podrías mostrar más información de la persona si lo deseas */}
    </div>
  );
}

export default Bienvenida;
