import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const [isOpen, setIsOpen] = useState(false);
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <>
      {/* Botón Hamburguesa */}
      <button 
        className={`hamburger-button ${isOpen ? 'open' : ''}`}
        onClick={toggleMenu}
        aria-label="Menú"
      >
        <span className="hamburger-line"></span>
        <span className="hamburger-line"></span>
        <span className="hamburger-line"></span>
      </button>

      {/* Menú Lateral */}
      <div className={`side-menu ${isOpen ? 'open' : ''}`}>
        <div className="menu-header">
          <h1 className="logo">S' FOR YOUR NUTRITION</h1>
          <h2 className="welcome-text">Bienvenido nuevamente</h2>
        </div>

        <nav className="navigation">
          <Link to="/productos" className="nav-link" onClick={toggleMenu}>Productos</Link>
          <Link to="/comprar" className="nav-link" onClick={toggleMenu}>Ordenar</Link>
          <Link to="/carrito" className="nav-link" onClick={toggleMenu}>Carrito</Link>
          <Link to="/mi-cuenta" className="nav-link" onClick={toggleMenu}>Mi Cuenta</Link>
        </nav>
      </div>

      {/* Overlay */}
      {isOpen && <div className="menu-overlay" onClick={toggleMenu} />}
    </>
  );
}

export default Menu;
