import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCarrito } from '../context/CarritoContext';
import '../style/pedidos.css';

function Pedidos() {
  const { carrito, vaciarCarrito, calcularTotal, actualizarCantidad, eliminarDelCarrito } = useCarrito();
  const [pedidos, setPedidos] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    cliente: '',
    productos: [],
    fecha: '',
    estado: 'pendiente'
  });
  const [todosIngredientes, setTodosIngredientes] = useState([]);
  const [editingProductIndex, setEditingProductIndex] = useState(null);
  const [editingProduct, setEditingProduct] = useState(null);
  const [filtroEstado, setFiltroEstado] = useState('todos');
  const [busqueda, setBusqueda] = useState('');

  // Cargar ingredientes disponibles
  useEffect(() => {
    fetch('http://localhost/back_your_nutrition/public/ingredientes')
      .then(response => response.json())
      .then(data => setTodosIngredientes(data))
      .catch(error => console.error('Error cargando ingredientes:', error));
  }, []);

  // Cargar pedidos desde localStorage al inicio
  useEffect(() => {
    const savedPedidos = JSON.parse(localStorage.getItem('pedidos')) || [];
    setPedidos(savedPedidos);
  }, []);

  // Guardar pedidos en localStorage cuando cambian
  useEffect(() => {
    localStorage.setItem('pedidos', JSON.stringify(pedidos));
  }, [pedidos]);

  // Registrar pedido desde el carrito
  const registrarPedidoDesdeCarrito = () => {
    if (carrito.length === 0) {
      alert('El carrito está vacío');
      return;
    }

    const nuevoPedido = {
      id: Date.now(),
      cliente: 'Cliente Actual',
      productos: carrito.map(item => ({
        ...item,
        ingredientesAdicionales: item.ingredientesAdicionales || []
      })),
      fecha: new Date().toISOString(),
      total: calcularTotal(),
      estado: 'pendiente'
    };

    setPedidos([nuevoPedido, ...pedidos]); // Agregar al inicio para verlo primero
    vaciarCarrito();
    alert('Pedido registrado exitosamente');
  };

  // Filtrar pedidos según estado y búsqueda
  const pedidosFiltrados = pedidos.filter(pedido => {
    const coincideEstado = filtroEstado === 'todos' || pedido.estado === filtroEstado;
    const coincideBusqueda = 
      pedido.id.toString().includes(busqueda) ||
      pedido.cliente.toLowerCase().includes(busqueda.toLowerCase()) ||
      pedido.productos.some(p => p.Nombre.toLowerCase().includes(busqueda.toLowerCase()));
    
    return coincideEstado && coincideBusqueda;
  });

  // Resto de tus funciones permanecen iguales (iniciarEdicion, guardarEdicion, etc.)
  // ... [Mantén todas tus funciones existentes aquí] ...

  return (
    <div className="pedidos-container">
      <div className="contenido-pedidos">
        <h2>Gestión de Pedidos</h2>
        
        <div className="controles-superiores">
          <div className="filtros-busqueda">
            <div className="filtro-estado">
              <label>Filtrar por estado:</label>
              <select
                value={filtroEstado}
                onChange={(e) => setFiltroEstado(e.target.value)}
              >
                <option value="todos">Todos</option>
                <option value="pendiente">Pendiente</option>
                <option value="confirmado">Confirmado</option>
                <option value="en preparación">En preparación</option>
                <option value="listo para entrega">Listo para entrega</option>
                <option value="entregado">Entregado</option>
                <option value="cancelado">Cancelado</option>
              </select>
            </div>
            
            <div className="busqueda">
              <label>Buscar:</label>
              <input
                type="text"
                placeholder="ID, cliente o producto..."
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
              />
            </div>
          </div>
          
          <button 
            onClick={registrarPedidoDesdeCarrito}
            className="btn-registrar"
            disabled={carrito.length === 0}
          >
            <i className="fas fa-cart-plus"></i> Registrar Pedido Actual
          </button>
        </div>

        <div className="resumen-pedidos">
          <div className="resumen-card total">
            <h4>Total Pedidos</h4>
            <p>{pedidos.length}</p>
          </div>
          <div className="resumen-card pendientes">
            <h4>Pendientes</h4>
            <p>{pedidos.filter(p => p.estado === 'pendiente').length}</p>
          </div>
          <div className="resumen-card preparacion">
            <h4>En preparación</h4>
            <p>{pedidos.filter(p => p.estado === 'en preparación').length}</p>
          </div>
          <div className="resumen-card entregados">
            <h4>Entregados</h4>
            <p>{pedidos.filter(p => p.estado === 'entregado').length}</p>
          </div>
        </div>

        <div className="lista-pedidos">
          {pedidosFiltrados.length === 0 ? (
            <div className="no-pedidos">
              <i className="fas fa-clipboard-list"></i>
              <p>No hay pedidos que coincidan con los filtros</p>
            </div>
          ) : (
            pedidosFiltrados.map(pedido => (
              <div key={pedido.id} className={`pedido-card ${pedido.estado}`}>
                {/* Mantén tu lógica de renderizado de pedido aquí */}
                {/* ... [Tu código existente de renderizado] ... */}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Pedidos;
