import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/notificaciones.css';

function Notificaciones() {
  const [nuevaNotificacion, setNuevaNotificacion] = useState({
    id: Date.now(),
    titulo: '',
    mensaje: '',
    tipo: 'info',
    fecha: new Date().toISOString(),
    leida: false,
    destinatario: 'cliente' // Puede ser 'cliente', 'admin' o 'todos'
  });

  const [notificaciones, setNotificaciones] = useState([]);

  // Cargar notificaciones al iniciar
  useEffect(() => {
    const notificacionesGuardadas = JSON.parse(localStorage.getItem('notificaciones')) || [];
    setNotificaciones(notificacionesGuardadas.filter(n => n.destinatario !== 'cliente'));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNuevaNotificacion(prev => ({
      ...prev,
      [name]: value,
      id: Date.now(),
      fecha: new Date().toISOString()
    }));
  };

  const crearNotificacion = () => {
    if (nuevaNotificacion.mensaje.trim() === '') return;
    
    const notificacionesActuales = JSON.parse(localStorage.getItem('notificaciones')) || [];
    const nuevasNotificaciones = [...notificacionesActuales, nuevaNotificacion];
    
    localStorage.setItem('notificaciones', JSON.stringify(nuevasNotificaciones));
    setNotificaciones(nuevasNotificaciones.filter(n => n.destinatario !== 'cliente'));
    
    // Resetear formulario
    setNuevaNotificacion({
      id: Date.now(),
      titulo: '',
      mensaje: '',
      tipo: 'info',
      fecha: new Date().toISOString(),
      leida: false,
      destinatario: 'cliente'
    });
    
    // Mostrar confirmación
    alert('Notificación creada y enviada al cliente');
  };

  const eliminarNotificacion = (id) => {
    const notificacionesActualizadas = notificaciones.filter(n => n.id !== id);
    localStorage.setItem('notificaciones', JSON.stringify(notificacionesActualizadas));
    setNotificaciones(notificacionesActualizadas);
  };

  return (
    <div className="notificaciones-admin">
      <nav className="menu-admin">
        {/* Tu menú de navegación existente */}
      </nav>

      <div className="contenido-notificaciones">
        <h1>Panel de Notificaciones</h1>
        
        <div className="crear-notificacion">
          <h2>Crear Nueva Notificación para Cliente</h2>
          <div className="form-group">
            <label>Título:</label>
            <input
              type="text"
              name="titulo"
              value={nuevaNotificacion.titulo}
              onChange={handleChange}
              placeholder="Título de la notificación"
            />
          </div>
          <div className="form-group">
            <label>Mensaje:</label>
            <textarea
              name="mensaje"
              value={nuevaNotificacion.mensaje}
              onChange={handleChange}
              placeholder="Escribe el mensaje para el cliente..."
              rows="4"
            />
          </div>
          <div className="form-group">
            <label>Tipo:</label>
            <select
              name="tipo"
              value={nuevaNotificacion.tipo}
              onChange={handleChange}
            >
              <option value="info">Información</option>
              <option value="alerta">Alerta</option>
              <option value="promocion">Promoción</option>
            </select>
          </div>
          <button onClick={crearNotificacion} className="btn-enviar">
            Enviar Notificación
          </button>
        </div>

        <div className="lista-notificaciones">
          <h2>Notificaciones Enviadas</h2>
          {notificaciones.length === 0 ? (
            <p>No hay notificaciones enviadas</p>
          ) : (
            <ul>
              {notificaciones.map(notif => (
                <li key={notif.id} className={`notificacion ${notif.tipo}`}>
                  <div className="notificacion-header">
                    <h3>{notif.titulo}</h3>
                    <span className="fecha">
                      {new Date(notif.fecha).toLocaleString()}
                    </span>
                  </div>
                  <p>{notif.mensaje}</p>
                  <div className="notificacion-acciones">
                    <button 
                      onClick={() => eliminarNotificacion(notif.id)}
                      className="btn-eliminar"
                    >
                      Eliminar
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Notificaciones;
