import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/register.css';

function RegistroCliente() {
  const [formData, setFormData] = useState({
    Documento: '',
    Nombres: '',
    Apellidos: '',
    Telefono: '',
    Email: '',
    Direccion: '',
    Id_ciudad: '',
    Contraseña: '',
    Id_tipo_compra: '',
    Puntos_fidelizacion: 0,
    Descuento_inicial: 0
  });

  const [tiposCompra, setTiposCompra] = useState([]);
  const navigate = useNavigate();

  // Cargar tipos de compra al montar el componente
  useEffect(() => {
    fetch('http://localhost:8000/tiposCompra')
      .then((response) => response.json())
      .then((data) => setTiposCompra(data))
      .catch((error) => {
        console.error('Error al cargar los tipos de compra:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Paso 1: Registrar persona
      const personaResponse = await fetch('http://localhost:8000/personas', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const personaData = await personaResponse.json();

      if (!personaResponse.ok) {
        throw new Error(personaData.error || 'Error al registrar persona');
      }

      const idPersona = personaData.Id;

      // Paso 2: Registrar cliente con el ID de persona
      const clienteResponse = await fetch('http://localhost:8000/clientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          Id_persona: idPersona,
          Id_tipo_compra: formData.Id_tipo_compra,
          Puntos_fidelizacion: formData.Puntos_fidelizacion,
          Descuento_inicial: formData.Descuento_inicial
        })
      });

      const clienteData = await clienteResponse.json();

      if (!clienteResponse.ok) {
        throw new Error(clienteData.error || 'Error al registrar cliente');
      }

      alert('Cliente registrado correctamente');
      setFormData({
        Documento: '',
        Nombres: '',
        Apellidos: '',
        Telefono: '',
        Email: '',
        Direccion: '',
        Id_ciudad: '',
        Contraseña: '',
        Id_tipo_compra: '',
        Puntos_fidelizacion: 0,
        Descuento_inicial: 0
      });

      navigate('/ingresar');
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Registro de Cliente</h2>
      <form onSubmit={handleSubmit}>
        <input type="number" name="Documento" placeholder="Documento" value={formData.Documento} onChange={handleChange} required />
        <input type="text" name="Nombres" placeholder="Nombres" value={formData.Nombres} onChange={handleChange} required />
        <input type="text" name="Apellidos" placeholder="Apellidos" value={formData.Apellidos} onChange={handleChange} required />
        <input type="number" name="Telefono" placeholder="Teléfono" value={formData.Telefono} onChange={handleChange} required />
        <input type="email" name="Email" placeholder="Email" value={formData.Email} onChange={handleChange} required />
        <input type="text" name="Direccion" placeholder="Dirección" value={formData.Direccion} onChange={handleChange} required />
        <input type="number" name="Id_ciudad" placeholder="Ciudad" value={formData.Id_ciudad} onChange={handleChange} required />
        <input type="password" name="Contraseña" placeholder="Contraseña" value={formData.Contraseña} onChange={handleChange} required />
        
        {/* Tipo de compra */}
        <select name="Id_tipo_compra" value={formData.Id_tipo_compra} onChange={handleChange} required>
          <option value="">Selecciona tipo de compra</option>
          {tiposCompra.map((tipo) => (
            <option key={tipo.Id} value={tipo.Id}>
              {tipo.Descripcion}
            </option>
          ))}
        </select>

        <input type="number" name="Puntos_fidelizacion" placeholder="Puntos de Fidelización" value={formData.Puntos_fidelizacion} onChange={handleChange} />
        <input type="number" step="0.01" name="Descuento_inicial" placeholder="Descuento Inicial (%)" value={formData.Descuento_inicial} onChange={handleChange} />

        <button type="submit">Registrar Cliente</button>
      </form>
    </div>
  );
}

export default RegistroCliente;
