import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../style/register.css';

function Menu() {
  const [isOpen, setIsOpen] = useState(false);
  const userRole = localStorage.getItem('userRole');
  const location = useLocation();

  if (userRole || location.pathname.startsWith('/admin') || location.pathname.startsWith('/cliente')) {
    return null;
  }

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <div className="app-container">
      {/* Menú Hamburguesa */}
      <div className="hamburger-menu" onClick={toggleMenu}>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
        <div className={`hamburger-line ${isOpen ? 'open' : ''}`}></div>
      </div>

      {/* Menú Lateral */}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="brand-header">
          <h1>S' FOR YOUR NUTRITION</h1>
          <h2>Bienvenido nuevamente</h2>
        </div>

        <div className="main-content">
          <div className="product-actions">
            <Link to="/productos" className="action-link">Productos</Link>
            <Link to="/ordenar" className="action-link">Ordenar</Link>
            <Link to="/carrito" className="action-link">Carrito</Link>
            <Link to="/mi-cuenta" className="action-link">Mi Cuenta</Link>
          </div>

          <div className="products-section">
            <h3>Nuestros Productos</h3>
            
            <div className="product-item">
              <div className="product-info">
                <span>Cupcake de vainilla</span>
                <span>$8500</span>
              </div>
            </div>

            <div className="product-item">
              <div className="product-info">
                <span>Cupcake de Avena</span>
                <span>$8000</span>
              </div>
            </div>

            <div className="product-item">
              <div className="product-info">
                <span>Malteada</span>
                <span>$12000</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Overlay */}
      {isOpen && <div className="overlay" onClick={toggleMenu} />}
    </div>
  );
}

export default Menu;
