import React from 'react';
import { useCarrito } from '../context/CarritoContext';
import '../style/batidos.css';
import { imagenes } from '../image';

function PaginaBatidos() {
  const [batidos, setBatidos] = React.useState([]);
  const { agregarAlCarrito } = useCarrito();

  React.useEffect(() => {
    fetch('http://localhost:8000/batidos')
      .then(res => res.json())
      .then(data => setBatidos(data))
      .catch(err => console.error("Error cargando batidos:", err));
  }, []);

  return (
    <div className="batidos-container">
      <h1>Nuestros Batidos</h1>
      <div className="batidos-grid">
        {batidos.map(batido => (
          <div 
            key={`batido-${batido.id}`}  // Usamos el ID del batido como key única
            className="batido-card"
            onClick={() => agregarAlCarrito(batido)}
          >
            <img 
              src={imagenes[batido.Nombre.toLowerCase()] || imagenes.default} 
              alt={batido.Nombre}
              className="batido-imagen"
            />
            <div className="batido-info">
              <h3>{batido.Nombre}</h3>
              <p>${batido.Precio.toFixed(2)}</p>
              <p className="batido-descripcion">{batido.Descripcion}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PaginaBatidos;
