import React, { useState, useEffect } from 'react';
import '../style/inventario.css';
import { Link } from 'react-router-dom';

const Inventario = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    Nombre: '',
    Precio: '',
    Cantidad: '0',
    Descripcion: '',
    Tamaño: '',
    Calorias: '',
    Disponibilidad: '1'
  });
  const [editandoId, setEditandoId] = useState(null);
  const [error, setError] = useState('');
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      setCargando(true);
      const res = await fetch('http://localhost:8000/productos');
      if (!res.ok) throw new Error(`Error: ${res.status}`);
      const data = await res.json();
      setProductos(data);
    } catch (err) {
      console.error("Error cargando productos:", err);
      setError("Error al cargar productos");
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validarFormulario = () => {
    const errores = [];
    if (!formData.Nombre.trim()) errores.push('Nombre es requerido');
    if (!formData.Precio || isNaN(formData.Precio)) errores.push('Precio inválido');
    if (!formData.Tamaño) errores.push('Seleccione tamaño');
    
    if (errores.length > 0) {
      setError(errores.join(', '));
      return false;
    }
    return true;
  };

  const iniciarEdicion = (producto) => {
    if (!producto?.id) {
      setError('Producto no válido para edición');
      return;
    }
    setEditandoId(producto.id);
    setFormData({
      Nombre: producto.Nombre || '',
      Precio: producto.Precio?.toString() || '',
      Cantidad: producto.Cantidad?.toString() || '0',
      Descripcion: producto.Descripcion || '',
      Tamaño: producto.Tamaño || '',
      Calorias: producto.Calorias?.toString() || '',
      Disponibilidad: producto.Disponibilidad?.toString() || '1'
    });
  };

  const cancelarEdicion = () => {
    setEditandoId(null);
    setFormData({
      Nombre: '',
      Precio: '',
      Cantidad: '0',
      Descripcion: '',
      Tamaño: '',
      Calorias: '',
      Disponibilidad: '1'
    });
    setError('');
  };

  const guardarProducto = async () => {
    if (!validarFormulario()) return;

    try {
      const productoData = {
        Nombre: formData.Nombre,
        Precio: parseFloat(formData.Precio),
        Cantidad: parseInt(formData.Cantidad),
        Descripcion: formData.Descripcion,
        Tamaño: formData.Tamaño,
        Calorias: formData.Calorias ? parseInt(formData.Calorias) : null,
        Disponibilidad: parseInt(formData.Disponibilidad)
      };

      let url = 'http://localhost:8000/productos';
      let method = 'POST';
      
      if (editandoId) {
        url += `/${editandoId}`;
        method = 'PUT';
      }

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(productoData)
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Error ${response.status}`);
      }

      const productoGuardado = await response.json();
      
      if (editandoId) {
        setProductos(productos.map(p => p.id === editandoId ? productoGuardado : p));
      } else {
        setProductos([...productos, productoGuardado]);
      }
      
      cancelarEdicion();
      alert(`Producto ${editandoId ? 'actualizado' : 'creado'} correctamente`);
    } catch (error) {
      console.error("Error guardando producto:", error);
      setError(error.message.includes('404') 
        ? 'Producto no encontrado en el servidor' 
        : error.message);
      
      if (error.message.includes('404')) {
        cargarProductos();
      }
    }
  };

  const eliminarProducto = async (id) => {
    if (!window.confirm('¿Eliminar este producto?')) return;
    
    try {
      const response = await fetch(`http://localhost:8000/productos/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error(`Error ${response.status}`);

      setProductos(productos.filter(p => p.id !== id));
      if (editandoId === id) cancelarEdicion();
      alert('Producto eliminado');
    } catch (error) {
      console.error("Error eliminando producto:", error);
      setError(error.message);
    }
  };

  if (cargando) return <div className="cargando">Cargando...</div>;

  return (
    <div className="inventario-container">
      {/* Header y navegación */}
      <h1>Inventario</h1>
      
      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>×</button>
        </div>
      )}

      {/* Formulario */}
      <div className="formulario-producto">
        {/* Campos del formulario */}
        {/* ... (tus campos existentes) ... */}
        
        <div className="form-actions">
          {editandoId ? (
            <>
              <button onClick={guardarProducto}>Guardar</button>
              <button onClick={cancelarEdicion}>Cancelar</button>
            </>
          ) : (
            <button onClick={guardarProducto}>Agregar</button>
          )}
        </div>
      </div>

      {/* Tabla de productos */}
      <div className="tabla-productos">
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Descripción</th>
              <th>Tamaño</th>
              <th>Calorías</th>
              <th>Cantidad</th>
              <th>Disponible</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {productos.length === 0 ? (
              <tr>
                <td colSpan="8">No hay productos</td>
              </tr>
            ) : (
              productos.map(producto => (
                <tr key={`producto-${producto.id}`}>
                  <td>{producto.Nombre}</td>
                  <td>${producto.Precio?.toFixed(2)}</td>
                  <td>{producto.Descripcion}</td>
                  <td>{producto.Tamaño} ml</td>
                  <td>{producto.Calorias || '-'}</td>
                  <td>{producto.Cantidad}</td>
                  <td>{producto.Disponibilidad ? 'Sí' : 'No'}</td>
                  <td>
                    <button onClick={() => iniciarEdicion(producto)}>Editar</button>
                    <button onClick={() => eliminarProducto(producto.id)}>Eliminar</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventario;
